        private void ClearFields(int NewValue)
        {
            /*
            11	Eröffnung Assessment
            1	Abschluss Beratung
            2	Kontaktabbruch
            3	Todesfall
            4	Wechsel des Wohnortes
            5	Triage an Dritte
            6	Triage an städt. Spezialdienste
            7	Weiterleitung an QT
            8	Abschluss Überbrückung ohne Assessment
            9	Wirtschaftliche Sozialhilfe abgelehnt / kein Anspruch
            10	Unterstützungsantrag zurückgezogen
            */
              // Felder löschen welche nicht verwendet werden
              if (NewValue != 7) // 1 = Weiterleitung an QT
              {
            qryFaCheckIn["FaRessourcenPaketCode"] = DBNull.Value;
            qryFaCheckIn["FaKriteriumCode"] = DBNull.Value;
            qryFaCheckIn["QTUebergabeDatum"] = DBNull.Value;
            qryFaCheckIn["QTName"] = DBNull.Value;
            qryFaCheckIn["QuartierTeamID"] = DBNull.Value;
            qryFaCheckIn["TaskID"] = DBNull.Value;
              }
              qryFaCheckIn.RefreshDisplay();
        }

        private void SetDefaultFields(int NewValue)
        {
            if (NewValue == 7 && DBUtil.IsEmpty(qryFaCheckIn["QuartierTeamID"]))  // Weiterleiten an ...
              {
            SqlQuery qry = DBUtil.OpenSQL(@"
              select HAU.OrgUnitID, Org.ItemName
              from vwPerson PRS
                LEFT JOIN BaHaus  HAU on HAU.BaStrasseID = PRS.WohnsitzStrasseCode
                  AND (CAST( HAU.Hausnummer as varchar ) + IsNull(HAU.Suffix,'')) = PRS.WohnsitzHausNr
                LEFT JOIN XOrgUnit ORG on ORG.OrgUnitID = HAU.OrgUnitID
              where BaPersonID = {0}",
              BaPersonID);
            qryFaCheckIn["QuartierTeamID"] = qry["OrgUnitID"];
            qryFaCheckIn["QTName"] = qry["ItemName"];
              }
        }

        private void SetEditFieldKontakt(int KontaktCode)
        {
            EditModeType EMode = EditModeType.ReadOnly;
              if ( CanEditData() &&
            KontaktCode == 2) EMode = EditModeType.Normal;
              edtKontaktPerson.EditMode = EMode;
        }

        private void SetEditField_Weiterleiten(int GrundCode)
        {
            EditModeType EMode = EditModeType.ReadOnly;
              bool CanEdit = (
            CanEditData() &&
            GrundCode == 7
              );
              if (CanEdit) EMode = EditModeType.Normal;

              edtDLP.EditMode = EMode;
              edtKriterium.EditMode = EMode;
              edtAbschlussAnTeam.EditMode = EMode;
              btnResCopy.Enabled = CanEdit && HasSpecialRights;
        }

        private void SetList_Kriterium(int aCode)
        {
            string oldCode = "";
              if (aCode > 0) oldCode = aCode.ToString();
              if (oldCode == edtKriterium.LOVFilter) return;

              edtKriterium.LOVFilter = oldCode;
              edtKriterium.LOVName = "FaKriterium";
        }

        private void btnResCopy_Click(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 07.12.2007 : sozheo : Neu: Pendenz erstellen
            // -------------------------------------------------------------

            if (!qryFaCheckIn.Post()) return;

            if (Utils.ConvertToInt(qryFaCheckIn["TaskID"]) > 0)
            {
              KissMsg.ShowInfo("Diese Pendenz wurde bereits erstellt.");
              return;
            }

            // Pendenz erstellen an Stellenleiter
            SqlQuery qrySL = DBUtil.OpenSQL(@"
              select top 1 ORG.ChiefID
              from FaLeistung                 FLE
                left outer join FaFall        FAL on FAL.FaFallID = FLE.FaFallID
                left outer join XOrgUnit_User OU on OU.UserID = FAL.UserID and OU.OrgUnitMemberCode = 2
                left outer join XOrgUnit      ORG on ORG.OrgUnitID = OU.OrgUnitID
              where FLE.FaLeistungID = {0}",
              faLeistungID);

            if (DBUtil.IsEmpty(qrySL["ChiefID"]))
            {
              KissMsg.ShowInfo("Der Stellenleiter ist nicht definiert.");
              return;
            }

            object taskID = DBUtil.ExecuteScalarSQL(@"
              Insert into XTask (
                TaskTypeCode, Subject, TaskStatusCode, CreateDate, StartDate, SenderID,
                TaskSenderCode, ReceiverID, TaskReceiverCode, JumpToPath,
                FaLeistungID
              ) values (
                5, 'Checkin an Stellenleiter', 1, {0}, {1}, {2}, 1, {3}, 1, {4}, {5} )
              select SCOPE_IDENTITY()",
              DateTime.Now, DateTime.Now, Session.User.UserID, qrySL["ChiefID"],
              FormController.ConvertToString(FormController.GetMessage("FrmFall", false, "Action", "GetJumpToPath") as System.Collections.Specialized.HybridDictionary),
              qryFaCheckIn["FaLeistungID"]);

            if (!DBUtil.IsEmpty(taskID))
            {
              qryFaCheckIn["TaskID"] = taskID;
              qryFaCheckIn["QTUebergabeDatum"] = DateTime.Today;
              qryFaCheckIn.Post();
              qryFaCheckIn_PositionChanged(sender, e);
              KissMsg.ShowInfo("Eine neue Pendenz wurde erstellt.");
            }
            else
            {
              KissMsg.ShowInfo("Fehler beim Erstellen der Pendenz.");
              return;
            }
        }

        private void edtAbschlussAnTeam_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            // Letzte Bearbeitung
            // 16.12.2007 : sozheo : Neu
            // ------------------------------------------------------------------

            string SearchString = edtAbschlussAnTeam.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                    SearchString = "%";
                else
                {
                    qryFaCheckIn["QuartierTeamID"] = DBNull.Value;
                    qryFaCheckIn["QTName"] = DBNull.Value;
                    return;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              select ID$ = Org.OrgUnitID, [Name] = Org.ItemName
              from  XOrgUnit Org
              where Org.ItemName like '%' + {0} + '%' order by [Name]",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
                qryFaCheckIn["QuartierTeamID"] = dlg[0];
                qryFaCheckIn["QTName"] =  dlg[1];
            }
        }

