            if (!edtZustaendigDatum.UserModified) return;
            string tmp = DBUtil.IsEmpty(edtZustaendigDatum.EditValue) ? "" : edtZustaendigDatum.EditValue.ToString();
            if (qryFaCheckIn["ZustaendigkeitGeprueftDatum"].ToString() == tmp) return;

