        private void qryFaCheckIn_BeforePost(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 28.08.2007 : sozheo : Alles neu
            // -------------------------------------------------------------

            DBUtil.CheckNotNullField(edtErstkontaktDatum, lblDatum.Text);
            DBUtil.CheckNotNullField(edtKontakt, lblKontakt.Text);

            // Wenn Abschlussdatum erfasst ist, dann ist der Kontaktgrund ein Muss-Feld:
            if (!DBUtil.IsEmpty(qryFaCheckIn["AbschlussDatum"]))
            {
              DBUtil.CheckNotNullField(edtKontaktGrund, lblKontaktGrund.Text);
              DBUtil.CheckNotNullField(edtZustaendigDatum, lblZustaendig.Text);
              DBUtil.CheckNotNullField(edtFallAbschlussGrund, "Fallabschlussgrund");
              if (DBUtil.IsEmpty(qryFaCheckIn["AbschlussUserID"]))
              {
                qryFaCheckIn["AbschlussUserID"] = Session.User.UserID;
                qryFaCheckIn["AbschlussLogonname"] = Session.User.LogonName;
              }
            }
        }

