        public void Init(string Name, Image TitleImage, int BaPersonID)
        {
            lblTitel.Text = Name;
            picTitel.Image = TitleImage;
            baPersonID = BaPersonID;

            qryPerson.Fill(this.baPersonID);

            kissSearch.SelectParameters = new object[] { BaPersonID };
            qryFallverlauf.Fill(kissSearch.SelectParameters);
            qryFallverlauf.EnableBoundFields(false);
        }

