        #region Private Methods

        private void CtlFaFallverlaufsprotokoll_Load(object sender, System.EventArgs e)
        {
            qryFallverlauf.Last();
        }

        private void edtUserIDX_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtUserIDX.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            KissLookupDialog dlg = new KissLookupDialog();
            if (e.ButtonClicked || !DBUtil.IsEmpty(SearchString))
            {
                if (DBUtil.IsEmpty(SearchString))
                    SearchString = "%";

                e.Cancel = !dlg.SearchData(@"
                    SELECT
                      ID$              = UserID,
                      UserID$          = UserID,
                      DisplayText$     = DisplayText,
                      OrgUnit$         = OrgUnit,
                      [Kürzel]         = LogonName,
                      [Mitarbeiter/in] = NameVorname,
                      [Org. Einheit]   = OrgUnit
                    FROM vwUser
                    WHERE DisplayText LIKE '%' + {0} + '%'
                    ORDER BY NameVorname
                    ", SearchString, e.ButtonClicked);

                if (e.Cancel) return;
            }

            edtUserIDX.EditValue = dlg["DisplayText$"];
            edtUserIDX.LookupID = dlg["UserID$"];
        }

        private void qryFallverlauf_AfterInsert(object sender, System.EventArgs e)
        {
            SqlQuery qry = DBUtil.OpenSQL(@"
              select top 1 FaFallID
              from   FaFall
              where  BaPersonID = {0}
              order by DatumVon DESC",
              baPersonID);

            qryFallverlauf["FaFallID"] = qry["faFallID"];
            qryFallverlauf["UserID"] = Session.User.UserID;
            qryFallverlauf["LogonName"] = Session.User.LogonName;
            qryFallverlauf["Mitarbeiter"] = Session.User.LogonName + " - " + Session.User.LastName + ", " + Session.User.FirstName;
            qryFallverlauf["ManuellerEintrag"] = true;
            qryFallverlauf["OrgUnitID"] = Session.User["OrgUnitID"];
            qryFallverlauf.EnableBoundFields(true);
            edtText.Focus();
        }

        private void qryFallverlauf_AfterPost(object sender, System.EventArgs e)
        {
            qryFallverlauf.EnableBoundFields(false);

            //quick hack
            qryFallverlauf.Fill(kissSearch.SelectParameters);
            qryFallverlauf.Last();
        }

        private void qryFallverlauf_BeforePost(object sender, System.EventArgs e)
        {
            DBUtil.CheckNotNullField(edtText, lblText.Text);
        }

