        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
              {
            case "BETRPERSONID" : return qryFaZielvereinbarung["BaPersonID"];
            case "FAFALLID" : return DBUtil.ExecuteScalarSQL(@"
                                    select FaFallID
                                    from   FaLeistung
                                    where  FaLeistungID = {0}",
                                    qryFaZielvereinbarung["FaLeistungID"]);
            case "FALLUSERID"   : return DBUtil.ExecuteScalarSQL(@"
                                    select FAL.UserID
                                    from   FaFall FAL
                                           inner join FaLeistung LEI on LEI.FaFallID = FAL.FaFallID
                                    where LEI.FaLeistungID = {0}",
                                    qryFaZielvereinbarung["FaLeistungID"]);
            case "FAZIELVEREINBARUNGID" : return qryFaZielvereinbarung["FaZielvereinbarungID"];
            case "ABSENDER"     : return 0;
              }
              return base.GetContextValue(FieldName);
        }

        public void Init(string Name, Image TitleImage, int LeistungID, int FaFallID)
        {
            lblTitel.Text = Name;
              picTitel.Image = TitleImage;
              faLeistungID = LeistungID;
              faFallID = FaFallID;

              // alle Personen aller Klientensysteme von BaPersonID
              qryPerson.Fill(faLeistungID);
              colPerson.ColumnEdit = grdZielvereinbarung.GetLOVLookUpEdit(qryPerson);

              // Daten ffnen:
              qryFaZielvereinbarung.Fill(faLeistungID);
        }

