        #region Public Methods

        public bool PersonSuchen(string Suchbegriff, object userID, bool open)
        {
            if (Suchbegriff == "") return true;

            string sql = @"
                select Klient = PRS.Name + isNull(' ' + PRS.Vorname,''),
                 Adresse = PRS.WohnsitzStrasseHausNr,
                 PLZOrt = PRS.WohnsitzPLZOrt,
                         LE = MOD.Text,
                         MA = USR.LastName + isNull(' ' + USR.Firstname,''),
                         FAL.*
                  from   FaFall FAL
                         inner join vwPerson PRS on PRS.BaPersonID = FAL.BaPersonID
                         inner join XUser     USR on USR.UserID = FAL.UserID
                         inner join XLOVCode  MOD on MOD.LOVName = 'Modul' and
                                              MOD.Code = FAL.ModulCode
                  where FAL.DatumBis is null AND FAL.UserID = {0} ";
            try
            {
                int Nr = System.Convert.ToInt32(Suchbegriff);
                sql += " AND PRS.BaPersonID = " + Nr.ToString();
            }
            catch
            {
                Suchbegriff = Suchbegriff.Replace("*","%").Replace(",","%").Replace(" ","%");

                sql += " AND PRS.Name + isNull(', ' + PRS.Vorname,'') like " + DBUtil.SqlLiteralLike(Suchbegriff + "%");
                sql += " order by PRS.Name + isNull(', ' + PRS.Vorname,'')";
            }
            qryPerson.Fill(sql, userID is int ? (int)userID: Session.User.UserID );

            if (qryPerson.Count == 0)
            {
                KissMsg.ShowInfo("DlgAuswahl", "KeinePersonGefunden", "Keine zutreffenden Personen gefunden");
                return false;
            }

            if ((qryPerson.Count == 1) && !open)
            {
                resultPersonRow = qryPerson.DataTable.Rows[0] ;
                return true;
            }

            return DisplayDialog() ;
        }

