        #region Private Methods

        // ComponentName: qryPerson
        private bool DisplayDialog()
        {
            if (ShowDialog(Session.MainForm) == DialogResult.OK)
            {
                resultPersonRow = qryPerson.Row;
                return true;
            }
            else
            {
                resultPersonRow = null;
                return false;
            }
        }

