		private void qryIkAnzeige_BeforePost(object sender, System.EventArgs e)
		{

			if (DBNull.Value == qryIkAnzeige["DatumVon"]) 
			{
				edtEroeffnet.Focus();
				throw new KissInfoException(KissMsg.GetMLMessage("CtlIkRechtlicheMassnahmenSchuldner", "EroeffnungsDatEingeben", "Bitte geben Sie das Eröffnungsdatum ein.", KissMsgCode.MsgInfo));
			}
			if (DBNull.Value == qryIkAnzeige["IkAnzeigeTypCode"]) 
			{
				edtAnzeigeTyp.Focus();
				throw new KissInfoException(KiSS4.DB.KissMsg.GetMLMessage("CtlIkRechtlicheMassnahmenSchuldner", "AnzeigetypWaehlen", "Bitte wählen Sie einen Anzeigetyp aus.", KissMsgCode.MsgInfo));
			}
			if (DBNull.Value != qryIkAnzeige["DatumBis"]) 
			{
			    if ((DateTime)qryIkAnzeige["DatumBis"] < (DateTime)qryIkAnzeige["DatumVon"]) 
				{
					edtAbgeschlossen.Focus();
					throw new KissInfoException(KissMsg.GetMLMessage("CtlIkRechtlicheMassnahmenSchuldner", "AbschlussDatVorEroeffnungsDat", "Das Abschlussdatum kann nicht vor dem Eröffnungsdatum liegen.", KissMsgCode.MsgInfo));
				}
			}
		}
	}
