SELECT IstGlaeubiger = CONVERT(BIT, 1), IstElternteil = GLB.IstElternteil, HatDaten = CONVERT(BIT, CASE WHEN EXISTS( SELECT TOP 1 1 FROM dbo.IkForderung FD WITH(READUNCOMMITTED) WHERE FD.IkRechtstitelID = GLB.IkRechtstitelID AND FD.BaPersonID = GLB.BaPersonID UNION ALL SELECT TOP 1 1 FROM dbo.IkPosition FD WITH(READUNCOMMITTED) INNER JOIN dbo.IkRechtstitelPosition RTP ON RTP.IkPositionID = FD.IkPositionID WHERE RTP.IkRechtstitelID = GLB.IkRechtstitelID AND FD.BaPersonID = GLB.BaPersonID UNION ALL SELECT TOP 1 1 FROM dbo.IkPosition FD WITH(READUNCOMMITTED) WHERE FD.Einmalig = 1 AND FD.IkRechtstitelID = GLB.IkRechtstitelID AND FD.BaPersonID = GLB.BaPersonID UNION ALL SELECT TOP 1 1 FROM dbo.IkVerrechnungskonto FD WITH(READUNCOMMITTED) WHERE FD.IkRechtstitelID = GLB.IkRechtstitelID AND FD.BaPersonID = GLB.BaPersonID ) THEN 1 ELSE 0 END), HatDatenEltern = CONVERT(BIT, CASE WHEN EXISTS( SELECT TOP 1 1 FROM dbo.IkForderung FD WITH(READUNCOMMITTED) WHERE FD.IkRechtstitelID = GLB.IkRechtstitelID AND FD.BaPersonID = GLB.BaPersonID ) THEN 1 ELSE 0 END), GLB.IkGlaeubigerID, GLB.IkRechtstitelID, GLB.BaPersonID, GLB.IkGlaeubigerStatusCode, OLD_IkGlaeubigerStatusCode = GLB.IkGlaeubigerStatusCode, Status = dbo.fnLOVText('IkGlaeubigerStatus', GLB.IkGlaeubigerStatusCode), GLB.IkGlaeubigerTS, PersonName = PRS.NameVorname + ISNULL(', '+CONVERT(VARCHAR, PRS.Geburtsdatum, 104)+'', ''), AnzahlRT = ( SELECT COUNT(*) FROM dbo.IkGlaeubiger G WITH(READUNCOMMITTED) LEFT JOIN dbo.IkRechtstitel R WITH(READUNCOMMITTED) ON R.IkRechtstitelID = G.IkRechtstitelID LEFT JOIN dbo.FaLeistung L WITH(READUNCOMMITTED) ON L.FaLeistungID = R.FaLeistungID WHERE L.FaFallID = {1} AND ( -- Bei KKBB sollen nur Rechtstitel KKBB angezeigt werden ({3} = 407 AND L.FaProzessCode = 407) OR -- Bei ALBV und UeBH sollen nur Rechtstitel ALBV und UeBH angezeigt werden ({3} IN (405, 406) AND L.FaProzessCode IN (405, 406) AND NOT R.IkRechtstitelGueltigBis IS NULL) ) AND G.BaPersonID = GLB.BaPersonID AND NOT R.IkRechtstitelID = {0} ), IstFallPerson = CONVERT(BIT, CASE WHEN GLB.BaPersonID = FAL.BaPersonID THEN 1 ELSE 0 END), PersGeburtsDatum = dbo.fnGetAge(PRS.Geburtsdatum, GETDATE()) FROM dbo.IkGlaeubiger GLB LEFT JOIN dbo.vwPerson PRS WITH(READUNCOMMITTED) ON PRS.BaPersonID = GLB.BaPersonID LEFT JOIN dbo.IkRechtstitel RTT WITH(READUNCOMMITTED) ON RTT.IkRechtstitelID = GLB.IkRechtstitelID LEFT JOIN dbo.FaLeistung LST WITH(READUNCOMMITTED) ON LST.FaLeistungID = RTT.FaLeistungID LEFT JOIN dbo.FaFall FAL WITH(READUNCOMMITTED) ON FAL.FaFallID = LST.FaFallID WHERE GLB.IkRechtstitelID = {0}
UNION ALL
SELECT IstGlaeubiger = CONVERT(BIT, 0), IstElternteil = CONVERT(BIT, 0), HatDaten = CONVERT(BIT, 0), HatDatenEltern = CONVERT(BIT, 0), NULL, NULL, FFP.BaPersonID, NULL, NULL, NULL, NULL, PersonName = PRS.NameVorname + ISNull(', '+CONVERT(VARCHAR, PRS.Geburtsdatum, 104)+'', ''), AnzahlRT = ( SELECT COUNT(*) FROM dbo.IkGlaeubiger G WITH(READUNCOMMITTED) LEFT JOIN dbo.IkRechtstitel R WITH(READUNCOMMITTED) ON R.IkRechtstitelID = G.IkRechtstitelID LEFT JOIN dbo.FaLeistung L WITH(READUNCOMMITTED) ON L.FaLeistungID = R.FaLeistungID WHERE L.FaFallID = {1} AND ( -- Bei KKBB sollen nur Rechtstitel KKBB angezeigt werden ({3} = 407 and L.FaProzessCode = 407) OR -- Bei ALBV und UeBH sollen nur Rechtstitel ALBV und UeBH angezeigt werden ({3} IN (405, 406) AND L.FaProzessCode IN (405, 406) AND NOT R.IkRechtstitelGueltigBis IS NULL) ) AND G.BaPersonID = FFP.BaPersonID AND NOT R.IkRechtstitelID = {0} ), IstFallPerson = CONVERT(BIT, CASE WHEN FFP.BaPersonID = FAL.BaPersonID THEN 1 ELSE 0 END), PersGeburtsDatum = dbo.fnGetAge(PRS.Geburtsdatum, GETDATE()) FROM dbo.FaFallPerson FFP WITH(READUNCOMMITTED) LEFT JOIN dbo.FaFall FAL WITH(READUNCOMMITTED) ON FAL.FaFallID = FFP.FaFallID LEFT JOIN dbo.vwPerson PRS WITH(READUNCOMMITTED) ON PRS.BaPersonID = FFP.BaPersonID WHERE FFP.FaFallID = {1} AND ({2}=1) AND NOT PRS.BaPersonID IN ( SELECT L.SchuldnerBaPersonID FROM dbo.IkRechtstitel R WITH(READUNCOMMITTED) LEFT JOIN dbo.FaLeistung L WITH(READUNCOMMITTED) ON L.FaLeistungID = R.FaLeistungID WHERE L.FaFallID = {1} AND L.FaProzessCode = {3} AND NOT L.SchuldnerBaPersonID IS NULL UNION ALL SELECT DISTINCT Q.BaPersonID FROM dbo.IkGlaeubiger Q WITH(READUNCOMMITTED) WHERE Q.IkRechtstitelID = {0} ) ORDER BY IstGlaeubiger DESC, IstFallPerson DESC, PersGeburtsDatum DESC, PersonName ASC
