        public void Init(
            string TitleName, Image TitleImage, int fallID, int LeistgID, int PersonID, string TreeID,
            int intProzessCode, bool LeistgGeschlossen)
        {
            faFallID = fallID;
            GlaeubigerBaPersonID = 0;
            SchuldnerBaPersonID = 0;
            FaLeistungID = LeistgID;

            if (TreeID.Contains("Schuldner"))
                // Schuldner ist gewählt
                SchuldnerBaPersonID = PersonID;
            else if (TreeID.Contains("Glaeubiger"))
                // Glaeubiger ist gewählt
                GlaeubigerBaPersonID = PersonID;
            else
                // Fallträger
                GlaeubigerBaPersonID = PersonID;

            if (intProzessCode == 405)
                edtForderungsart.LOVFilter = "A";
            else if (intProzessCode == 406)
                edtForderungsart.LOVFilter = "U";
            else if (intProzessCode == 407)
                edtForderungsart.LOVFilter = "K";
            else if (intProzessCode == 301 || intProzessCode == 302 || intProzessCode == 304)
                edtForderungsart.LOVFilter = "I";
            else
                edtForderungsart.LOVFilter = "E";
            edtForderungsart.LOVName = "IkForderungArt";

            if (intProzessCode < 400 || intProzessCode == 408 || intProzessCode == 409 || intProzessCode == 410)
            {
                lblGlaeubiger.Visible = false;
                edtGlaeubiger.Visible = false;
            }

            if (TreeID.Contains("Glaeubiger")) colGlaeubiger.Visible = false;

            picTitel.Image = TitleImage;
            lblTitel.Text  = TitleName;

            qrySchuldnerSuchen.Fill(faFallID);

            qryGlaeubigerSuchen.Fill(faFallID);
            if (GlaeubigerBaPersonID > 0) edtGlaeubiger.EditValue = GlaeubigerBaPersonID;

            // #4857: Änderungen für W-Inkasso
            if (intProzessCode == 301 || intProzessCode == 302 || intProzessCode == 304) // W-Inkasso
            {
                colSoll.Caption = "Verpflichtungen";
                colSoll.Width = 105;
                colHaben.Caption = "Zahlungen";
                colSaldo.Caption = "Rückstand";
                grvKontoauszug.OptionsView.ShowFooter = true;
                wik = true;
                if (SchuldnerBaPersonID > 0)
                    edtSchuldner.EditValue = SchuldnerBaPersonID;
            }
            //tabBgPosition.TabIndex = wik ? 1 : 0;
            tabBgPosition.SelectTab(wik ? tpgBudgetblatt : tpgBelege);
            tabBgPosition.HideTabs = !wik;
            FillQueries();
            SetEditMode();
        }

