        public override object GetContextValue(string FieldName)
        {
            switch (FieldName.ToUpper())
            {
                case "FAFALLID":
                    return this.faFallID;
                case "DATUMVON":
                    return DBUtil.IsEmpty(edtDatumVon.EditValue) ? 0 : edtDatumVon.EditValue;
                case "DATUMBIS":
                    return DBUtil.IsEmpty(edtDatumBis.EditValue) ? 0 : edtDatumBis.EditValue;
                case "FORDERUNGSART":
                    return DBUtil.IsEmpty(edtForderungsart.EditValue) ? 0 : edtForderungsart.EditValue;
                case "SCHULDNERBAPERSONID":
                    return DBUtil.IsEmpty(edtSchuldner.EditValue) ? 0 : edtSchuldner.EditValue;
                case "GLAEUBIGERBAPERSONID":
                    return DBUtil.IsEmpty(edtGlaeubiger.EditValue) ? 0 : edtGlaeubiger.EditValue;
                case "FALEISTUNGID":
                    return this.FaLeistungID;
                case "WIK":
                    return wik;
                case "ABSENDER":
                    SqlQuery qryUser = DBUtil.OpenSQL("Select USR.UserID, USR.LogonName FROM FaLeistung LEI WITH (READUNCOMMITTED) INNER JOIN XUser USR WITH (READUNCOMMITTED) ON LEI.UserID = USR.UserID WHERE LEI.FaLeistungID = {0}", this.FaLeistungID);
                    int userid = (int)qryUser["UserID"];
                    return userid;

            }
            return base.GetContextValue(FieldName);
        }

