        private void qryAusgleich_AfterFill(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 17.06.2008 : sozheo : Stornierte Buchungen nicht berücksichtigen
            // ------------------------------------------------------------------
            // Saldo berechnen
            decimal Saldo = (decimal)0.0;
            foreach (System.Data.DataRow row in qryAusgleich.DataTable.Rows)
            {
                // ist nicht notwendig, da eine Gegenbuchung existieren sollte
                //if (Utils.ConvertToInt(row["KbBuchungStatusCode"]) == 8)
                  // Stornierte nicht berücksichtigen
                //  row["Saldo"] = DBNull.Value;

                if (DBUtil.IsEmpty(row["BetragSoll"]))
                {
                    Saldo = Saldo - Utils.ConvertToDecimal(row["BetragHaben"]);
                    row["Saldo"] = Saldo;
                }
                else
                {
                    Saldo = Saldo + Utils.ConvertToDecimal(row["BetragSoll"]);
                    row["Saldo"] = Saldo;
                }
            }
        }

        private void qryKontoauszug_AfterFill(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 17.06.2008 : sozheo : Stornierte Buchungen nicht berücksichtigen
            // ------------------------------------------------------------------
            // Detail-Daten öffnen
            //qryKontoauszug.Last();
            //funzt nicht:
            //qryKontoauszug.Position = qryKontoauszug.Count - 1;

            if (qryKontoauszug.Count == 0) qryKontoauszug_PositionChanged(sender, e);

            // Saldo berechnen
            decimal Saldo = (decimal)0.0;
            decimal Diff = (decimal)0.0;
            foreach (System.Data.DataRow row in qryKontoauszug.DataTable.Rows)
            {
                // ist nicht notwendig, da eine Gegenbuchung existieren sollte
                //if (Utils.ConvertToInt(row["KbBuchungStatusCode"]) == 8)
                    // Stornierte nicht berücksichtigen
                    //row["Saldo"] = DBNull.Value;
                //else
                if (!DBUtil.IsEmpty(row["BetragHaben"]) && !Utils.ConvertToBool(row["IstZahlung"], false))
                {
                    Saldo = Saldo - Utils.ConvertToDecimal(row["BetragHaben"]);
                    row["Saldo"] = Saldo;
                }

                if (!DBUtil.IsEmpty(row["BetragSoll"]) && !Utils.ConvertToBool(row["IstZahlung"], false))
                {
                    Saldo = Saldo + Utils.ConvertToDecimal(row["BetragSoll"]);
                    row["Saldo"] = Saldo;
                }

                if (DBUtil.IsEmpty(row["BetragHaben"]))
                {
                    Diff = Utils.ConvertToDecimal(row["BetragSoll"]) - Utils.ConvertToDecimal(row["Bezahlt"]);
                    row["Differenz"] = Diff;
                }
            }
        }

