        private void grvKontoauszug_CustomSummaryCalculate(object sender, DevExpress.Data.CustomSummaryEventArgs e)
        {
            try
            {
                if (wik && e.Item == this.colSaldo.SummaryItem && e.SummaryProcess == DevExpress.Data.CustomSummaryProcess.Calculate)
                {
                    e.TotalValue = (e.TotalValue == null || e.TotalValue == System.DBNull.Value ? decimal.Zero : (decimal)e.TotalValue)
                           + (this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragSoll"] == System.DBNull.Value || this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragSoll"] == null ? decimal.Zero : (decimal)this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragSoll"])
                           - (this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragHaben"] == System.DBNull.Value || this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragHaben"] == null ? decimal.Zero : (decimal)this.grvKontoauszug.GetDataRow(e.RowHandle)["BetragHaben"]);
                }
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo(ex.Message);
            }
        }

        private void qryDokumente_AfterInsert(object sender, EventArgs e)
        {
            qryDokumente["Stichwort"] = "Kontoauszug";
            qryDokumente["FaLeistungID"] = this.FaLeistungID;
            qryDokumente["DatumErstellung"] = DateTime.Today;
            qryDokumente["FaDokStatusCode"] = 2; // aktuell
            qryDokumente["FaDokVerwendungCode"] = 3; // intern
            qryDokumente["FaDokThemaCode"] = 8; // inkasso
            qryDokumente["BaPersonID"] = SchuldnerBaPersonID;
            qryDokumente["ErstelltUserID"] = Session.User.UserID;
            qryDokumente["ErstelltDatum"] = DateTime.Now;
            qryDokumente["MutiertUserID"] = Session.User.UserID;
            qryDokumente["MutiertDatum"] = DateTime.Now;
            qryDokumente["Absender"] = -Session.User.UserID;
            qryDokumente.RowModified = true;
        }

        private void SetEditMode()
        {
            if (qryDokumente.Count > 0 && (DBUtil.IsEmpty(qryDokumente["FaDokStatusCode"]) || (qryDokumente["FaDokStatusCode"] as int? != 4))) // 4: historisiert
            {
                edtDokStichworte.EditMode = EditModeType.Normal;
                docKontoauszug.EditMode = EditModeType.Normal;
            }
            else
            {
                edtDokStichworte.EditMode = EditModeType.ReadOnly;
                docKontoauszug.EditMode = EditModeType.ReadOnly;
            }
        }

        private void qryDokumente_ColumnChanged(object sender, DataColumnChangeEventArgs e)
        {
            SetEditMode();
        }

        private void docKontoauszug_DocumentCreated(object sender, EventArgs e)
        {
            try
            {
                foreach(DataRow row in qryDokumente.DataTable.Rows)
                    if(row["DocumentID"].Equals(qryDokumente["DocumentID"]))
                    {
                        row["DateCreation"] = DateTime.Now;
                        row["DateLastSave"] = DateTime.Now;
                        qryDokumente.DataTable.AcceptChanges();
                        qryDokumente.RefreshDisplay();
                        break;
                    }
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo(ex.Message);
            }
        }

