        private bool CanEdit
        {
            get
            {
                bool Erledigt = Utils.ConvertToBool(qryIkPosition["ErledigterMonat"], false);
                return (
                    qryIkPosition.Count>0 &&
                    qryIkPosition.CanUpdate &&
                    FaLeistungID>0 &&
                    CanEditRTitel &&
                    !Erledigt &&
                    !LeistungIstGeschlossen
                );
            }
        }

