        private bool hatAVerwendungsPeriode(int TCode)
        {
            return (
                TCode ==  1  || // 1	Kinderalimente
                TCode ==  2  || // 2	Kinderalimente
                TCode ==  3  || // 3	Erwachsenenalimente
                TCode ==  4  || // 4	Erwachsenenalimente
                TCode ==  5  || // 5	Kinderzulagen
                TCode ==  6  || // 6	Kinderzulagen
                TCode ==  7  || // 7	ALBV
                TCode == 10  || // 10	UeBH
                TCode == 11  || // 11	KKBB
                
                // #4767, 22.07.2009 :
                // Bei der Erfassung von Betreibungskosten, Gerichtskosten und Prozessentschädigung 
                // z.G. Gläubiger und Stadt Zürich, muss nun zwingend die Verwendungsperiode erfasst werden, 
                // was falsch ist.
                /*
                TCode == 12  || // Betreibungskosten Gläubiger 922 Saldo per
                TCode == 212 || // Betreibungskosten Stadt Zürich 912 Saldo per
                TCode == 16  || // Gerichtskosten Gläubiger 922 Saldo per
                TCode == 216 || // Gerichtskosten Stadt Zürich 912 Saldo per
                TCode == 14  || // Prozessentschädigung Stadt Zürich 914 Saldo per
                TCode == 214    // Prozessentschädigung Gläubiger 922 Saldo per
                 * */
                TCode == 13  || // 213 Verzugszinsen Stadt Zürich
                TCode == 213    // 13 Verzugszinsen Gläubiger
                

            );
        }

