        private void qryZVVTarife_BeforePost(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 05.02.2009 : sozheo : neu
            // ------------------------------------------------------------------------

            // Kontrolle der Zonen
            int Zone = Utils.ConvertToInt(qryZVVTarife["IkZVVTarifeCode"]);
            if (Zone < 1 || Zone > 6) throw new KissInfoException("Es sind nur die Zonen 1 bis 6 zulässig.");

            // Text im Gitter updaten
            if (qryLOV.Find("Code=" + Utils.ConvertToInt(qryZVVTarife["IkZVVTarifeCode"]).ToString()))
              qryZVVTarife["ZoneText"] = Utils.ConvertToString(qryLOV["Text"]);
            else
              qryZVVTarife["ZoneText"] = "[ungültig]";
        }

