        private void CtlIkZahlungslaufValuta_Load(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 22.05.2008 : sozheo : neu
            // ---------------------------------------------------

            SqlQuery qry = DBUtil.OpenSQL(@"
                SELECT Code = Jahr FROM dbo.IkZahlungslaufValuta
                UNION SELECT Year(GetDate())
                UNION SELECT Year(GetDate()) + 1
                UNION SELECT Year(GetDate()) + 2");

            ddlYear.Properties.DataSource = qry;
            ddlYear.EditValue = DateTime.Today.Year;

            FillData();
        }

        // Letzte Bearbeitung
        // 22.05.2008 : sozheo : neu
        // ---------------------------------------------------
        private void FillData()
        {
            DBUtil.ExecSQL(@"
            IF (SELECT COUNT(*) FROM dbo.IkZahlungslaufValuta WHERE Jahr = {0}) < 12 BEGIN
              INSERT INTO dbo.IkZahlungslaufValuta (Monat, Jahr)
            SELECT Monat, {0}
            FROM (SELECT Monat = 1
                  UNION ALL SELECT  2
                  UNION ALL SELECT  3
                  UNION ALL SELECT  4
                  UNION ALL SELECT  5
                  UNION ALL SELECT  6
                  UNION ALL SELECT  7
                  UNION ALL SELECT  8
                  UNION ALL SELECT  9
                  UNION ALL SELECT 10
                  UNION ALL SELECT 11
                  UNION ALL SELECT 12) TMP
            WHERE NOT EXISTS (SELECT IkZahlungslaufvalutaID FROM dbo.IkZahlungslaufValuta WHERE Jahr = {0} AND Monat = TMP.Monat)
            END",
                ddlYear.EditValue);

            qryZahlungslauf.Fill(ddlYear.EditValue);
        }

        private void ddlYear_EditValueChanged(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 22.05.2008 : sozheo : neu
            // ---------------------------------------------------

            FillData();
        }

