        /// <summary>
        /// Sets the date range.
        /// </summary>
        private void SetDateRange()
        {
            if (qryPeriodeX.Count > 0)
            {
                editDatumVonX.DateTime = (DateTime)qryPeriodeX["PeriodeVon"];
                editDatumBisX.DateTime = (DateTime)qryPeriodeX["PeriodeBis"];
            }
        }

        /// <summary>
        /// Shows the time periods.
        /// </summary>
        private void ShowPerioden()
        {
            qryPeriodeX.Fill(@"
                SELECT PER.*, STA.Text StatusText
                FROM         KbPeriode PER
                INNER JOIN   XLOVCode  STA ON  STA.Code = PER.PeriodeStatusCode
                                           AND STA.LOVName = 'KbPeriodeStatus'
                WHERE 1 = 1
                      AND KbMandantId = {0}
                ORDER BY PeriodeVon",
                KbMandantId);

            if (qryPeriodeX.Count > 0)
            {
                qryPeriodeX.Last();
                SetDateRange();
            }
        }

