        #region EventHandlers

        private void qryKreditor_PositionChanged(object sender, System.EventArgs e)
        {
            if (qryKreditor["BaPersonID"] is int)
            {
                ctlBaZahlungsweg.BaPersonID = (int)qryKreditor["BaPersonID"];
            }
            else if (qryKreditor["BaInstitutionID"] is int)
            {
                ctlBaZahlungsweg.BaInstitutionID = (int)qryKreditor["BaInstitutionID"];
            }
        }

        #endregion

        #region Methods

        #region Public Methods

        public override bool ReceiveMessage(System.Collections.Specialized.HybridDictionary parameters)
        {
            base.ReceiveMessage(parameters);

            NewSearch();
            edtSearchBaPersonID.EditValue = parameters["BaPersonID"];
            edtSearchBaInstitutionID.EditValue = parameters["BaInstitutionID"];
            tabControlSearch.SelectTab(tpgListe);

            FormController.SendMessage(ctlBaZahlungsweg, parameters);

            return true;
        }

        #endregion

        #region Protected Methods

        protected override void NewSearch()
        {
            ctlBaZahlungsweg.Visible = false;
            base.NewSearch();
            edtSearchPerson.Checked = true;
            edtSearchInstitution.Checked = true;
            edtSearchName.Focus();
        }

        protected override void RunSearch()
        {
            base.RunSearch();
            ctlBaZahlungsweg.Visible = true;
        }

