            this.kbPeriodeID = (int) FormController.GetMessage("FrmKbKlientenbuchhaltung", false, "Action", "KbPeriodeID");

            this.periodeVon = Utils.ConvertToDateTime(
                                DBUtil.ExecuteScalarSQL(@"SELECT PeriodeVon
                                                          FROM KbPeriode
                                                          WHERE KbPeriodeID = {0}", this.kbPeriodeID));
            this.periodeBis = Utils.ConvertToDateTime(
                                DBUtil.ExecuteScalarSQL(@"SELECT PeriodeBis
                                                          FROM KbPeriode
                                                          WHERE KbPeriodeID = {0}", this.kbPeriodeID));

            this.kissSearch.SelectParameters = new object[] {kbPeriodeID, Session.User.LanguageCode};
            grdQuery1.DataSource = null;

            this.NewSearch();
        }

        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            grdQuery1.DataSource = qryQuery;
        }

        private void grdQuery1_XtraPrint(object sender, XtraPrintEventArgs e)
        {
            string footerTextLeft = Utils.GetDateRangeText(edtBelegDatumVonX.Text, edtBelegDatumBisX.Text);
            grdQuery1.SetHeaderAndFooterText(
                e,
                KissMsg.GetMLMessage("CtlQueryKbAbrechnungASVS", "KbAbrechnungASV", "Abrechnung ASV"),
                footerTextLeft
            );
        }

        #endregion
    }
}