        #region Private Methods

        private void CtlQueryKbKostenstellenjournal_Load(object sender, System.EventArgs e)
        {
            try
            {
                this.kbPeriodeID = (int) FormController.GetMessage("FrmKbKlientenbuchhaltung", false, "Action", "KbPeriodeID");
            }
            catch
            {
                this.kbPeriodeID = 36;
            }

            kissSearch.SelectParameters = new object[] { kbPeriodeID };
            grdQuery1.DataSource = null;
        }

        private void edtKostenstelleX_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            DlgAuswahl dlg = new Common.DlgAuswahl();

            e.Cancel = !dlg.KbKostenstelleSuchen(edtKostenstelleX.Text, e.ButtonClicked);

            if (!e.Cancel)
            {
                edtKostenstelleX.EditValue = dlg["Kostenstelle"];
                edtKostenstelleX.LookupID  = dlg["BaPersonID"];
            }
        }

        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            grdQuery1.DataSource = qryQuery;
        }

        private void grdQuery1_XtraPrint(object sender, XtraPrintEventArgs e)
        {
            string footerTextLeft = Utils.GetDateRangeText(edtBuchungsDatumVon.Text, edtBuchungsDatumBis.Text);
            grdQuery1.SetHeaderAndFooterText(
                e,
                KissMsg.GetMLMessage("CtlQueryKbKostenstellenJournal", "KbKostenstellenJournal", "Kostenstellenjournal"),
                footerTextLeft
            );
        }

