        private void CalculateTotal()
        {
            int Count = 0;
            decimal Total = 0;

            foreach (DataRow R in qryKbBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    Count++;
                    Total += (decimal)R["BetragAbs"];
                }
            }

            edtTotal.EditValue = Total;
            edtAnzahl.EditValue = Count;

            btnTransfer.Enabled = (
              HasRights &&
              Count > 0
            );
        }

        private void CtlIkZahllauf_Load(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 12.06.2008 : sozheo : Neu
            // 25.06.2008 : sozheo : Neue Rechte
            // 01.08.2008 : sozheo : Spalte Selektion sperren
            // 18.11.2008 : sozngq : Buchungsdatum (NOW + 1 Werktag) setzen
            // ------------------------------------------------------------------------
            DateTime d = DateTime.Today.AddDays(1); //heute + 1 Tage
            if ((int)d.DayOfWeek == 6) d = d.AddDays(2); //Samstag -> Montag
            if ((int)d.DayOfWeek == 0) d = d.AddDays(1); //Sonntag -> Montag
            dateBuchungsdatum.EditValue = d;

            if (modul == ModulID.I)
            {
                // A Zahlungslauf
                edtModul.EditValue = 4;
                HasRights = DBUtil.UserHasRight("CtlIkZahllauf_A");
            }
            else if (modul == ModulID.S)
            {
                // WIK Zahlungslauf
                edtModul.EditValue = 3;
                HasRights = DBUtil.UserHasRight("CtlIkZahllauf_WIK");
            }
            colSelektion.OptionsColumn.AllowEdit = HasRights;
            btnTransfer.Visible = HasRights;
            btnAlle.Visible = HasRights;
            btnKeine.Visible = HasRights;

            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL("select * from dbo.XLOVCode where LOVName = 'KbBuchungsStatus' order by SortKey");
            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            edtSucheZahlungForderung.LoadQuery(DBUtil.OpenSQL(@"
                select Code = convert(int,null), Text = ''
                union all
                select Code = 1, Text = 'Zahlung an Kreditor'
                union all
                select Code = 2, Text = 'Forderung an Debitor'"));

            grvZahllauf.CellValueChanging += new DevExpress.XtraGrid.Views.Base.CellValueChangedEventHandler(grvZahllauf_CellValueChanging);
            grvZahllauf.CustomRowCellEdit += new DevExpress.XtraGrid.Views.Grid.CustomRowCellEditEventHandler(this.grvZahllauf_CustomRowCellEdit);

            NewSearch();
        }

        private void ProgressStart()
        {
            Cursor = Cursors.WaitCursor;
            ApplicationFacade.DoEvents();
            int successCount = 0;
            int failureCount = 0;
            int count = 0;
            int id = 0;

            DlgProgressLog.AddLine("Start Übertragung");
            foreach (DataRow R in qryKbBuchung.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"]) && (bool)R["Sel"])
                {
                    count++;
                    id = (int)R["KbBuchungID"];
                    try
                    {
                        bool success = false;
                        if ((int)R["AuszahlungsArtCode"] != 103)
                        {
                            // Dies ist ein Barbeleg, d.h. wir verwenden das aktuelle Tagesdatum
                            success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitAlimBuchungsstoff(new int[] { id }, DateTime.Today);
                        }
                        else if (R["ZahlungForderung"].ToString() == "Z" && dateBuchungsdatum.EditValue != null)  // Z = Zahlung = Kreditor
                        {
                            // Dies ist ein Kreditor, und das ValutaDatum ist gesetzt, somit verwende die entsprechende PSCD Methode mit ValutaDatum
                            success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitAlimBuchungsstoff(new int[] { id }, dateBuchungsdatum.DateTime);
                        }
                        else
                        {
                            success = KiSS4.FAMOZ.PSCDServices.PSCDInterface.SubmitAlimBuchungsstoff(new int[] { id });
                        }
                        if (success)
                            successCount++;
                        else
                            failureCount++;
                    }
                    catch (Exception ex)
                    {
                        DlgProgressLog.AddError(ex.Message);
                        failureCount++;
                    }
                    if (count % 50 == 0)
                    {
                        DlgProgressLog.AddLine(string.Format("{0} Belege bearbeitet", count));
                    }
                }
            }
            CalculateTotal();
            if (successCount == 1 && failureCount == 0)
                DlgProgressLog.AddLine("Auszahl-/Forderungsbeleg wurde erfolgreich übermittelt!");
            else if (successCount > 1 && failureCount == 0)
                DlgProgressLog.AddLine(string.Format("{0} Auszahl/Forderungsbelege wurden erfolgreich übermittelt!", successCount));
            else
                DlgProgressLog.AddLine(string.Format("{0} Auszahl/Forderungsbeleg(e) wurde(n) erfolgreich übermittelt{1}{2} Auszahl/Forderungsbeleg(e) konnte(n) nicht übermittelt werden", successCount, Environment.NewLine, failureCount));

            qryKbBuchung.Refresh();
            Cursor = Cursors.Default;
            DlgProgressLog.EndProgress();
            DlgProgressLog.ShowTopMost();
        }

