        private void btnInsert_Click(object sender, System.EventArgs e)
        {
            DBUtil.ExecSQLThrowingException(@"
                INSERT INTO KbBuchungKostenart (KbBuchungID, BgPositionID, BgKostenartID, BaPersonID, Buchungstext, Betrag, KontoNr, Hauptvorgang, Teilvorgang, Belegart, VerwPeriodeVon, VerwPeriodeBis)
                SELECT {0}, BPO.BgPositionID, BKA.BgKostenartID, BPO.BaPersonID, BKA.Name, 0, BKA.KontoNr, BKA.Hauptvorgang, BKA.Teilvorgang, BKA.Belegart, BPO.VerwPeriodeVon, BPO.VerwPeriodeBis
                FROM vwBgPosition           BPO
                  INNER JOIN BgPositionsart BPA ON BPA.BgPositionsartID = BPO.BgPositionsartID
                  INNER JOIN BgKostenart    BKA ON BKA.BgKostenartID    = BPA.BgKostenartID
                WHERE BPO.BgPositionID = {1}",
                    qryKbBuchung["KbBuchungID"],
                    qryBgPosition["BgPositionID"]);

            qryKbBuchung.Refresh();
        }

        private void qryKbBuchung_PositionChanged(object sender, System.EventArgs e)
