        #region Private Methods

        private void SelectionChanged()
        {
            bool allowStatusEdit = true;
            bool allowStornoVerstecken = true;
            Decimal selTotal = 0;
            int selAnzahl = 0;
            foreach(DataRow row in qryKbWVMigEinzelposten.DataTable.Rows)
            {
                if( (bool)row["Sel"] )
                {
                    selTotal = selTotal + (Decimal)row["Betrag"];
                    selAnzahl++;

                    if( (int)row["KbWVEinzelpostenStatusCode"] != 5 /*Abgesetzt - in Abklärung*/)
                    {
                        allowStatusEdit = false;
                    }

                    if ((int)row["KbWVEinzelpostenStatusCode"] != 7 /*storniert*/)
                    {
                        // Mind. ein selektierte WV-EP ist nicht im Status 7
                        allowStornoVerstecken = false;
                    }
                }
            }

            if (selAnzahl > 0)
            {
                lblTotalSelektierteUndBetrag.Text = "Selektierte Belege: " + selAnzahl + ", Betragstotal = " + selTotal.ToString("C");
            }
            else
            {
                allowStatusEdit = false;
                allowStornoVerstecken = false;
                lblTotalSelektierteUndBetrag.Text = "Keine Belege selektiert";
            }

            btnStornoVerstecken.Enabled = allowStornoVerstecken;

            edtStatus.EditMode = allowStatusEdit ? EditModeType.Normal : EditModeType.ReadOnly;
            btnStatusSetzen.Enabled = allowStatusEdit;
        }

        private void SelectionChanging(object sender, DevExpress.XtraEditors.Controls.ChangingEventArgs e)
        {
            qryKbWVMigEinzelposten.Row["Sel"] = e.NewValue;
            SelectionChanged();
        }

        private void btnAlle_Click(object sender, System.EventArgs e)
        {
            foreach(DataRow row in qryKbWVMigEinzelposten.DataTable.Rows)
            {
                row["Sel"] = true;
            }
            SelectionChanging(null,new DevExpress.XtraEditors.Controls.ChangingEventArgs(false,true));
        }

        private void btnKeine_Click(object sender, System.EventArgs e)
        {
            foreach(DataRow row in qryKbWVMigEinzelposten.DataTable.Rows)
            {
                row["Sel"] = false;
            }
            SelectionChanging(null,new DevExpress.XtraEditors.Controls.ChangingEventArgs(true, false));
        }

        private void btnStatusSetzen_Click(object sender, System.EventArgs e)
        {
            if( edtStatus.EditValue == DBNull.Value || edtStatus.EditValue == null )
            {
                KissMsg.ShowError("CtlKbWVMigrierteEinzelposten","StatusLeer","Bitte Status auswählen");
                return;
            }

            List<int> ids = new List<int>();

            foreach(DataRow row in qryKbWVMigEinzelposten.DataTable.Rows)
            {
                if( (bool)row["Sel"] )
                {
                    ids.Add((int)row["MigWVEinzelpostenID"]);
                }
            }

            DBUtil.ExecSQLThrowingException(string.Format(@"
              UPDATE MigWVEinzelposten
              SET KbWVEinzelpostenStatusCode = {1}
              WHERE MigWVEinzelpostenID IN ({0})", string.Join(",", ids.ConvertAll<string>(Convert.ToString).ToArray()),edtStatus.EditValue));

            qryKbWVMigEinzelposten.Refresh();
        }

        private void btnStornoVerstecken_Click(object sender, System.EventArgs e)
        {
            if (KissMsg.ShowQuestion("Sollen die selektierten WV-EPs mit Status S ausgeblendet werden? Achtung: Falls ja, sind diese WV-EPs nicht mehr aufrufbar!"))
            {
                // OK, verstecke die selektierten WV-EPs mit Status S
                List<int> ids = new List<int>();

                foreach(DataRow row in qryKbWVMigEinzelposten.DataTable.Rows)
                {
                    if( (bool)row["Sel"] && (int)row["KbWVEinzelpostenStatusCode"] == 7 /*storniert*/)
                    {
                        ids.Add((int)row["MigWVEinzelpostenID"]);
                    }
                }

                DBUtil.ExecSQLThrowingException(string.Format(@"
                  UPDATE MigWVEinzelposten
                  SET KbWVEinzelpostenStatusCode = 700 -- 700 => Storniert versteckt (hidden) 'H'
                  WHERE MigWVEinzelpostenID IN ({0})", string.Join(",", ids.ConvertAll<string>(Convert.ToString).ToArray()),edtStatus.EditValue));

                qryKbWVMigEinzelposten.Refresh();

                SelectionChanged();
            }
        }

