        private void edtInstitution_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtInstitution.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    KissMsg.ShowInfo("Es kann nicht der ganze Institutionenstamm angezeigt werden. Bitte Suchbegriff eingeben!");
                }
                    else
                {
                    qryZahlungseingang["BaInstitutionID"] = DBNull.Value;
                        qryZahlungseingang["Debitor"] = DBNull.Value;
                }
                return;
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT ID$                 = INS.BaInstitutionID,
                     Institution         = INS.Name,
                     Adresse             = INS.Adresse,
                     Typ                 = INS.Typ,
                     InstitutionTypCode$ = INS.InstitutionTypCode,
                     OrtStrasse$         = INS.OrtStrasse
              FROM   vwInstitution INS
              WHERE  INS.Name LIKE '%' + {0} + '%'
              ORDER BY INS.Name",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
                qryZahlungseingang["BaInstitutionID"] = dlg["ID$"];
                qryZahlungseingang["Institution"]     = dlg["Institution"];
                    qryZahlungseingang["Debitor"]         = dlg["Institution"] + ", " + dlg["Adresse"];
            }
        }

        private void edtKlient_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtKlient.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                    else
                {
                    qryZahlungseingang["BaPersonID"] = DBNull.Value;
                        qryZahlungseingang["Klient"] = DBNull.Value;
                }
                return;
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT BaPersonID$  = PRS.BaPersonID,
                     [Pers.-Nr]   = PRS.BaPersonID,
                     Name         = PRS.Name,
                     Vorname      = PRS.Vorname,
                     Wohnsitz     = PRS.Wohnsitz,
                     Klient$      = PRS.NameVorname
              FROM   vwPerson PRS
                     inner join FaLeistung LEI on LEI.BaPersonID = PRS.BaPersonID and
                                                  LEI.FaProzessCode = 500
              WHERE  PRS.NameVorname like '%' + {0} + '%'
              ORDER BY PRS.NameVorname",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
                qryZahlungseingang["BaPersonID"] = dlg["KgKontoID$"];
                   	qryZahlungseingang["Klient"] = dlg["Klient$"];;
            }
        }

        private void edtKonto_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            string SearchString = edtKonto.EditValue.ToString().Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "%";
                }
                    else
                {
                    qryZahlungseingang["KgKontoID"] = DBNull.Value;
                        qryZahlungseingang["Konto"] = DBNull.Value;
                }
                return;
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT KgKontoID$  = KTO.KgKontoID,
                     [Konto-Nr.] = KTO.KontoNr,
                     Name        = KTO.KontoName,
                     Konto$      = KTO.KontoNr + ' ' + KTO.KontoName
              FROM   KgKonto KTO
              WHERE  KTO.KgPeriodeID is null and
                     KTO.KontoGruppe = 0 and
                     KTO.KontoNr + KTO.KontoName LIKE '%' + {0} + '%'
              ORDER BY KTO.KontoNr",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
                qryZahlungseingang["KgKontoID"] = dlg["KgKontoID$"];
                   	qryZahlungseingang["Konto"] = dlg["Konto$"];;
            }
        }

