        public override bool OnAddData()
        {
            // avoid crash in case of too fast user
            if (this.treKgKonto.DataSource == null)
            {
                throw new KissCancelException();
            }

            if (!qryKgKonto.Post()) return false;

            // create a new row and imediately save it to database because of problems with tree

            // get parent id from tree and calc next sortkey
            if (this.treKgKonto.FocusedNode != null)
            {
                this.gruppeKontoID = (int)this.treKgKonto.FocusedNode.GetValue("KgKontoID");
                this.kgKontoklasseCode = (int)this.treKgKonto.FocusedNode.GetValue("KgKontoklasseCode");
                this.sortKey = (int)DBUtil.ExecuteScalarSQL(@"
                    SELECT isNull(MAX(SortKey)+1,0)
                                FROM   KgKonto
                                WHERE  GruppeKontoID = {0}", this.gruppeKontoID);
            }

            if (this.gruppeKontoID < 0 || this.sortKey < 0)
            {
                KissMsg.ShowInfo(KissMsg.GetMLMessage("CtlKgKonto", "InvalidParentIDOrSortKeyInsert", "Ungültiger Wert für gruppeKontoID='{0}' oder SortKey='{1}', Datensatz kann nicht erstellt werden.", KissMsgCode.MsgError, this.gruppeKontoID, this.sortKey));
                return false;
            }

            treKgKonto.DataSource = null;

            // apply values to new row
            qryKgKonto.Insert();
            qryKgKonto["GruppeKontoID"] = gruppeKontoID;
            qryKgKonto["SortKey"] = sortKey;
            qryKgKonto["KontoGruppe"] = false;
            qryKgKonto["KontoNr"] = "0";
            qryKgKonto["KontoName"] = "Neues Konto";
            qryKgKonto["KgPeriodeID"] = (baPersonID == 0) ? DBNull.Value : qryPeriode["KgPeriodeID"];
            qryKgKonto["KgKontoklasseCode"] = this.kgKontoklasseCode;
            qryKgKonto["KgKontoartCode"] = 1; // Normal
            qryKgKonto["Vorsaldo"] = 0F;
            qryKgKonto["VorsaldoUebertrag"] = false;
            qryKgKonto.Post();

            ShowKonten((int)qryKgKonto["KgKontoID"]);
            edtKontoNr.Focus();

            // reset properties
            gruppeKontoID = -1;
            this.kgKontoklasseCode = -1;
            sortKey = -1;

            // handle controls
            //this.RefreshEnabledStates();

            return true;
        }

        public override bool OnDeleteData()
        {
            bool result = false;
            if (this.HasChildren((int)qryKgKonto["KgKontoID"]))
            {
                if (KissMsg.ShowQuestion("CtlKgKonto", "DeleteSubTreeItems", "Soll die Kontogruppe samt Unterkonten gelöscht werden?"))
                {
                    DeleteKgKonto((int)qryKgKonto["KgKontoID"]);
                    result = true;
                }
                else
                {
                    return false;
                }
            }
            else
            {
                result = base.OnDeleteData();
            }

            if (qryKgKonto.Count > 0)
            {
                ShowKonten((int)qryKgKonto["KgKontoID"]);
            }
            else
            {
                ShowKonten(-1);
            }

            return result;
        }

