        private void RefreshEnabledStates()
        {
            if (this.qryKgKonto.Row.RowState == DataRowState.Added)
            {
                this.btnUp.Enabled = false;
                this.btnDown.Enabled = false;
                this.btnLeft.Enabled = false;
                this.btnRight.Enabled = false;
            }
            else
            {
                // get current active row
                DataRow row = this.qryKgKonto.Row;

                // check if we have an id
                if (DBUtil.IsEmpty(row["KgKontoID"]))
                {
                    return;
                }

                // get values
                int kgKontoID = (int)row["KgKontoID"];
                int parentItemID = row["GruppeKontoID"] == System.DBNull.Value ? -1 : (int)row["GruppeKontoID"];

                // handle root-items, static-items, system-flag and navbar flags
