        #region Private Methods

        private void SetMemoText()
        {
            memInfo.Text = "";
              int Code = Utils.ConvertToInt(edtVorgang.EditValue);
              int Typ = Utils.ConvertToInt(edtTyp.EditValue);
              switch (Code)
              {
            case 1:
              if (Typ == 1)
                memInfo.Text =
                "Wenn zuviel ausbezahlt wurde, kann der Differenzbetrag mit einer "+
                "Verrechnung im nächsten Monat ausgeglichen werden.\r\n"+
                "Definieren Sie den Differenzbetrag.\r\n"+
                "Eine neue Verrechnung wird erstellt.";
              else if (Typ == 2)
                memInfo.Text =
                "Wenn zuwenig ausbezahlt wurde, kann der Differenzbetrag mit einer "+
                "Verrechnung im nächsten Monat ausgeglichen werden.\r\n"+
                "Definieren Sie den Differenzbetrag und das Valutadatum.\r\n"+
                "Eine neue Verrechnung wird erstellt.";
              break;

            case 2:
              memInfo.Text =
                "Korrigieren Sie die Forderungen, welche den falschen Betrag enthalten.\r\n"+
                "Die Korrektur erfolgt sodann über die Saldo-Funktionalität.";
              break;

            case 3:
              memInfo.Text = "Text 3";
              break;

            case 4:
              memInfo.Text = "Text 4";
              break;

            case 5:
              memInfo.Text = "Text 5";
              break;
              }
              //memInfo.Font = new System.Font("Arial", 12, false);
        }

        private void edtTyp_EditValueChanged(object sender, System.EventArgs e)
        {
            SetMemoText();
        }

        private void edtVorgang_EditValueChanged(object sender, System.EventArgs e)
        {
            edtTyp.Enabled = (Utils.ConvertToInt(edtVorgang.EditValue) == 1);
            edtTyp.EditValue = 0;
            edtBetrag.Enabled = (Utils.ConvertToInt(edtVorgang.EditValue) == 1);
            btnExecute.Enabled = (Utils.ConvertToInt(edtVorgang.EditValue) == 1);

            SetMemoText();
        }

