data.value:
DECLARE @Data Table( FaLeistungID INT, Datum DATETIME, MelderIn INT, NSB VARCHAR(100), Thema VARCHAR(100), Auflagendatum DATETIME )
---------------------------------- ---------- Collect Data ---------------------------------- INSERT INTO @Data SELECT GME.FaLeistungID, GME.DatumEingang, GME.FaKontaktveranlasserCode, GME.VmGefaehrdungNSBCodes, GME.FaThemaCodes, GME.AuflageDatum FROM dbo.VmGefaehrdungsMeldung GME INNER JOIN dbo.FaLeistung FAL WITH (READUNCOMMITTED) ON FAL.FaLeistungID = GME.FaLeistungID INNER JOIN dbo.XUser USR WITH (READUNCOMMITTED) ON USR.UserID = FAL.UserID LEFT JOIN dbo.XOrgUnit_User OUU WITH (READUNCOMMITTED) ON OUU.UserID = USR.UserID AND OUU.OrgUnitMemberCode = 2 LEFT JOIN dbo.XOrgUnit ORG WITH (READUNCOMMITTED) on ORG.OrgUnitID = OUU.OrgUnitID --- AND ORG.OrgUnitID = {edtOrgUnitID}
WHERE CONVERT(DATETIME, GME.DatumEingang) BETWEEN {edtDatumVon} AND {edtDatumBis} AND GME.IsDeleted = 0 --- AND ORG.OrgUnitID = {edtOrgUnitID} --- AND USR.UserID = {edtUserID.LookupID}
------------------------------------------ ------------ Liste 1: Übersicht alle Fälle ------------------------------------------ SELECT BaPersonID$ = PRS.BaPersonID, Sektion = ORG.ItemName, SAR = USR.LastName + ISNULL(', ' + USR.FirstName,''), Person = PRS.Name + ISNULL(', ' + PRS.Vorname,''), [Alter] = dbo.fngetage(PRS.Geburtsdatum, GETDATE()), Geschlecht = case PRS.GeschlechtCode when 1 then 'm' when 2 then 'f' else '?' end, Nationalität = LAN.Text, Datum = DAT.Datum, MelderIn = dbo.fnLovText('FaKontaktveranlasser', DAT.MelderIn), NSB = dbo.fnLovTextListe('VmGefaehrdungNSB', DAT.NSB), Thema = dbo.fnLovTextListe('FaThema', DAT.Thema), Auflage = DAT.Auflagendatum FROM @Data DAT INNER JOIN dbo.FaLeistung FAL WITH (READUNCOMMITTED) ON FAL.FaLeistungID = DAT.FaLeistungID INNER JOIN dbo.BaPerson PRS WITH (READUNCOMMITTED) ON PRS.BaPersonID = FAL.BaPersonID INNER JOIN dbo.XUser USR WITH (READUNCOMMITTED) ON USR.UserID = FAL.UserID LEFT JOIN dbo.XOrgUnit_User OUU WITH (READUNCOMMITTED) ON OUU.UserID = USR.UserID AND OUU.OrgUnitMemberCode = 2 LEFT JOIN dbo.XOrgUnit ORG WITH (READUNCOMMITTED) on ORG.OrgUnitID = OUU.OrgUnitID LEFT JOIN dbo.BaLand LAN WITH (READUNCOMMITTED) ON PRS.NationalitaetCode = LAN.BaLandID ORDER BY Sektion, SAR, Person
