data.value:
-- Zuerst werden alle unterschiedlichen Personenids gesucht und in die Tabelle PersID gespeichert. -- Die eigentliche Abfrage durchsucht dann die Daten anhand dieser IDs DECLARE @LanguageCode INT; SET @LanguageCode = {0};
DECLARE @PersID TABLE( BaPersonID INT )
INSERT @PersID SELECT DISTINCT PRS.BaPersonID FROM BaPerson PRS LEFT JOIN FaLeistung LEI ON LEI.BaPersonID = PRS.BaPersonID LEFT JOIN XUser BEN ON BEN.UserID = LEI.UserID WHERE 1=1 --- AND LEI.ModulID = {edtModulID} --- AND ({edtNurAktive} = 0 OR LEI.DatumBis IS NULL) --- AND BEN.UserID = {edtUserID.LookupID}
-- Wohnsitz Adresse SELECT Anrede = PRS.Titel, Name = PRS.Name, Vorname = PRS.Vorname, Strasse = PRS.WohnsitzStrasseHausNr, PLZ = PRS.WohnsitzPLZ, Ort = PRS.WohnsitzOrt, Heimatort = PRS.Heimatort, PRS.HeimatgemeindeBaGemeindeID, [Nationalität] = CASE @LanguageCode WHEN 1 THEN PRS.Nationalitaet WHEN 2 THEN PRS.NationalitaetFR WHEN 3 THEN PRS.NationalitaetIT ELSE PRS.Nationalitaet END, Geschlecht = dbo.fnLOVMLText('Geschlecht', PRS.GeschlechtCode, @LanguageCode),
BaPersonID$ = PRS.BaPersonID FROM @PersID PID INNER JOIN vwPerson PRS ON PRS.BaPersonID = PID.BaPersonID where 1 = 1 --- and Name like {edtName} + '%' --- and Vorname like {edtVorname} + '%' --- and PRS.NationalitaetCode = {edtNationalitaetCode} --- and PRS.GeschlechtCode like {edtGeschlechtCode} + '%' --- and PRS.Heimatort like {edtHeimatort} + '%' --- and PRS.WohnsitzStrasse like {edtStrasse} + '%' --- and PRS.WohnsitzPLZ like {edtPLZ} + '%' --- and PRS.WohnsitzOrt like {edtOrt} + '%' --- and PRS.WohnsitzKanton like {edtKanton} + '%'
-- Aufenthalt Adresse SELECT Anrede = PRS.Titel, Name = PRS.Name, Vorname = PRS.Vorname, [Auf. Str.] = PRS.AufenthaltStrasseHausNr, [Auf. PLZ] = PRS.AufenthaltPLZ, [Auf. Ort] = PRS.AufenthaltOrt, Heimatort = PRS.Heimatort, [Nationalität] = CASE @LanguageCode WHEN 1 THEN PRS.Nationalitaet WHEN 2 THEN PRS.NationalitaetFR WHEN 3 THEN PRS.NationalitaetIT ELSE PRS.Nationalitaet END, Geschlecht = dbo.fnLOVMLText('Geschlecht', PRS.GeschlechtCode, @LanguageCode), BaPersonID$ = PRS.BaPersonID FROM @PersID PID INNER JOIN VWPerson PRS ON PRS.BaPersonID = PID.BaPersonID where 1 = 1 --- and Name like {edtName} + '%' --- and Vorname like {edtVorname} + '%' --- and PRS.NationalitaetCode = {edtNationalitaetCode} --- and PRS.GeschlechtCode like {edtGeschlechtCode} + '%' --- and PRS.Heimatort like {edtHeimatort} + '%' --- and PRS.AufenthaltStrasse like {edtAufStrasse} + '%' --- and PRS.AufenthaltPLZ like {edtAufPLZ} + '%' --- and PRS.AufenthaltOrt like {edtAufOrt} + '%' --- and PRS.AufenthaltKanton like {edtAufKanton} + '%'
