data.value:
SELECT [BaPersonID$] = LEI.BaPersonID, [Name] = PRS.Name, [Vorname] = PRS.Vorname, [Geburtsdatum] = PRS.Geburtsdatum, [Nationalität] = PRS.Nationalitaet, [Deutschkenntnisse] = dbo.fnLOVText('KaDeutschkenntnisse', VPF.DeutschMuendlichCode), [Eröffnung] = LEI.DatumVon, [Bereiche] = CASE WHEN COALESCE(VPF.BrancheCodes, VPF.KaBetriebCodes, VPF.EinsatzbereichCodes) IS NULL THEN NULL ELSE dbo.fnLOVMLColumnListe('KaBranche', VPF.BrancheCodes, 'Value1', 1) + ';' + dbo.fnLOVMLColumnListe('KaVermittlungKaBetriebe', VPF.KaBetriebCodes, 'Value1', 1) + ';' + dbo.fnLOVMLColumnListe('KaVermittlungEinsatzbereich', VPF.EinsatzbereichCodes, 'Value1', 1) END, [Mögl. Pensum] = ISNULL( ISNULL( CONVERT(VARCHAR(3), VPF.EinsatzpensumVon) + ISNULL('-' + CONVERT(VARCHAR(3), VPF.EinsatzpensumBis), '') + ' %', ''), CONVERT(VARCHAR(3), VPF.EinsatzpensumBis) + ' %'), [Vermittelbarkeit] = dbo.fnLOVText('KaVermittlungSiVermittelbarkeit', VPF.VermittelbarkeitSICode), [Einsatzort] = KAB.BetriebName, [Pensum] = VEI.Arbeitspensum, [Einsatz ab] = VEI.EinsatzVon, [Einsatzende] = VEI.Abschluss, [Vermittelt] = ISNULL(VSI.Vermittelt, CONVERT(BIT, 0)), [Kategorie] = dbo.fnLOVText('KaSiKategorie', VSI.KaSiKategorieCode), [Zuständig SAR] = USR2.NameVorname, [Zuständig KA] = USR.NameVorname FROM dbo.FaLeistung LEI WITH (READUNCOMMITTED) INNER JOIN dbo.vwPerson PRS WITH (READUNCOMMITTED) ON PRS.BaPersonID = LEI.BaPersonID INNER JOIN dbo.vwUser USR WITH (READUNCOMMITTED) ON USR.UserID = LEI.UserID -- KA User LEFT JOIN dbo.KaVermittlungSI VSI WITH (READUNCOMMITTED) ON VSI.FaLeistungID = LEI.FaLeistungID LEFT JOIN dbo.FaLeistung LEI2 WITH (READUNCOMMITTED) ON LEI2.BaPersonID = LEI.BaPersonID AND LEI2.ModulID = 2 -- Fallführung AND (LEI2.DatumBis IS NULL OR LEI2.DatumBis >= GETDATE()) LEFT JOIN dbo.vwUser USR2 WITH (READUNCOMMITTED) ON USR2.UserID = LEI2.UserID -- SAR LEFT JOIN dbo.KaVermittlungProfil VPF WITH (READUNCOMMITTED) ON VPF.FaLeistungID = LEI.FaLeistungID
OUTER APPLY (SELECT TOP 1 VOR.KaEinsatzplatzID, VEI.Arbeitspensum, VEI.EinsatzVon, VEI.Abschluss FROM KaVermittlungVorschlag VOR INNER JOIN KaVermittlungEinsatz VEI ON VEI.KaVermittlungVorschlagID = VOR.KaVermittlungVorschlagID --uns interessieren nur Vorschläge, welche auch einen Einsatz haben WHERE VOR.FaLeistungID = LEI.FaLeistungID AND VEI.EinsatzVon IS NOT NULL --uns interessieren nur Vorschläge, welche auch einen Einsatz haben ORDER BY VEI.EinsatzVon DESC --Relevant ist der aktuellste erfasste Einsatz ) VEI
LEFT JOIN dbo.KaEinsatzplatz KEI WITH (READUNCOMMITTED) ON KEI.KaEinsatzplatzID = VEI.KaEinsatzplatzID LEFT JOIN dbo.KaBetrieb KAB WITH (READUNCOMMITTED) ON KAB.KaBetriebID = KEI.KaBetriebID WHERE LEI.ModulID = 7 -- KA AND LEI.FaProzessCode = 706 -- Vermittlung SI AND (LEI.DatumBis IS NULL OR LEI.DatumBis >= GETDATE()) ---AND VSI.Vermittelt = {edtSucheStatusVermittlung} ---AND VSI.KaSiKategorieCode = {edtSucheKategorien} ---AND LEI.UserID = {edtSucheZustaendigKA.LookupID} ORDER BY PRS.NameVorname, LEI.DatumVon DESC, VEI.EinsatzVon DESC;
