DECLARE @DatumVon DATETIME; DECLARE @DatumBis DATETIME;
SET @DatumVon = '17530101'; SET @DatumBis = '99991230'; --- SET @DatumVon = {edtDatumVon}; --- SET @DatumBis = {edtDatumBis}; SET @DatumBis = DATEADD(s, -1, DATEADD(d, 1, @DatumBis)); -- unmittelbar vor Mitternacht
IF object_id('tempdb..#Faelle') IS NOT NULL DROP TABLE #Faelle
SELECT SAR = USR.LastName + ISNULL(', ' + USR.FirstName, ''), DatumVon, DatumBis INTO #Faelle FROM XUser USR INNER JOIN FaLeistung LEI ON LEI.UserID = USR.UserID WHERE 1 = 1 AND ISNULL(LEI.DatumBis, '99991231') >= @DatumVon AND LEI.DatumVon <= @DatumBis --- AND USR.UserID = {edtUserID.LookupID} --- AND LEI.ModulID = {edtModulID} --- AND ({edtNurAktive} = 0 OR LEI.DatumBis IS NULL) --- AND {edtBfsLeistungsart} = LEI.LeistungsartCode AND LEI.ModulID = 3
SELECT [SAR] = SAR.SAR, [Eröffnet] = (SELECT COUNT(*) FROM #Faelle WHERE SAR = SAR.SAR AND DatumVon BETWEEN @DatumVon AND @DatumBis), [Geschlossen] = (SELECT COUNT(*) FROM #Faelle WHERE SAR = SAR.SAR AND DatumBis BETWEEN @DatumVon AND @DatumBis), [Total aktive] = (SELECT COUNT(*) FROM #Faelle WHERE SAR = SAR.SAR) FROM (SELECT DISTINCT SAR FROM #Faelle) SAR UNION ALL SELECT NULL, NULL, NULL, NULL UNION ALL SELECT [SAR] = 'Total', [Eröffnet] = (SELECT COUNT(*) FROM #Faelle WHERE DatumVon BETWEEN @DatumVon AND @DatumBis ), [Geschlossen] = (SELECT COUNT(*) FROM #Faelle WHERE DatumBis BETWEEN @DatumVon AND @DatumBis ), [Total aktive] = (SELECT COUNT(*) FROM #Faelle);
IF object_id('tempdb..#Faelle') IS NOT NULL DROP TABLE #Faelle;
