data.value:
DECLARE @Data Table( FaLeistungID INT, Grund INT )
---------------------------------- ---------- Collect Data ---------------------------------- INSERT INTO @Data SELECT FAL_V.FaLeistungID, Grund = FAL_V.AbschlussgrundCode FROM dbo.FaLeistung FAL_V WITH (READUNCOMMITTED) INNER JOIN dbo.XUser USR WITH (READUNCOMMITTED) ON USR.UserID = FAL_V.UserID LEFT JOIN dbo.XOrgUnit_User OUU WITH (READUNCOMMITTED) ON OUU.UserID = USR.UserID AND OUU.OrgUnitMemberCode = 2 LEFT JOIN dbo.XOrgUnit ORG WITH (READUNCOMMITTED) ON ORG.OrgUnitID = OUU.OrgUnitID WHERE FAL_V.ModulID = 5 -- Vormundschaft AND FAL_V.FaProzessCode = 505 -- Vormundschaftlicher Auftrag AND {edtAuswertungsjahr} = YEAR(FAL_V.DatumBis) -- Fall im Auswertungsjahr abgeschlossen AND OUU.OrgUnitID IN (SELECT OrgUnitID FROM dbo.XOrgUnit WITH (READUNCOMMITTED) WHERE ModulID = 3 AND ParentID = 28) -- es sollen nur Einträge der Sektion Jugendamt angezeigt werden --- AND OUU.OrgUnitID = {edtOrgUnitID} --- AND USR.UserID = {edtUserID.LookupID}
---------------------------------- ---------- List 1 ---------- Data overview ---------------------------------- SELECT BaPersonID$ = PRS.BaPersonID, Sektion = ORG.ItemName, SAR = USR.LastName + ISNULL(', ' + USR.FirstName,''), Person = PRS.Name + ISNULL(', ' + PRS.Vorname,''), [Alter] = dbo.fngetage(PRS.Geburtsdatum, GETDATE()), Geschlecht = CASE PRS.GeschlechtCode WHEN 1 THEN 'm' WHEN 2 THEN 'f' ELSE '?' END, [Nationalität] = LAN.Text, Abschluss = FAL.DatumBis, Grund = dbo.fnLovText('VmAuftragAbschlussgrund', DAT.Grund) FROM @Data DAT INNER JOIN dbo.FaLeistung FAL WITH (READUNCOMMITTED) ON FAL.FaLeistungID = DAT.FaLeistungID INNER JOIN dbo.BaPerson PRS WITH (READUNCOMMITTED) ON PRS.BaPersonID = FAL.BaPersonID INNER JOIN dbo.XUser USR WITH (READUNCOMMITTED) ON USR.UserID = FAL.UserID LEFT JOIN dbo.XOrgUnit_User OUU WITH (READUNCOMMITTED) ON OUU.UserID = USR.UserID AND OUU.OrgUnitMemberCode = 2 LEFT JOIN dbo.XOrgUnit ORG WITH (READUNCOMMITTED) ON ORG.OrgUnitID = OUU.OrgUnitID LEFT JOIN dbo.BaLand LAN WITH (READUNCOMMITTED) ON PRS.NationalitaetCode = LAN.BaLandID ORDER BY 2,3,4
