data.value:
declare @Aktuell table ( UserID int, BaPersonID int, ZGBCodes varchar(100), Stichdatum datetime, FalltypCode int, BewilligtCode int, Tage int, VmBewertungID int ) insert @Aktuell select UserID = FAL.UserID, BaPersonID = FAL.BaPersonID, ZGBCodes = VMN.ZGBCodes, Stichdatum = convert(datetime,STI.Text + convert(varchar,VBW.VmFallgewichtungJahr),104), FallTypCode = VBW.VmMandatstypCode, BewilligtCode = VBW.VmMandatstypBewilligtCode, Tage = datediff(d,convert(datetime,STI.Text + convert(varchar,VBW.VmFallgewichtungJahr),104),getdate()), VBW.VmBewertungID from dbo.FaLeistung FAL WITH (READUNCOMMITTED) LEFT JOIN dbo.VmBewertung VBW WITH (READUNCOMMITTED) on VBW.FaLeistungID = FAL.FaLeistungID and VBW.VmBewertungID = (select top 1 VmBewertungID from dbo.VmBewertung WITH (READUNCOMMITTED) where FaLeistungID = FAL.FaLeistungID and VmMandatstypCode is not null order by VmFallgewichtungJahr desc,VmFallgewichtungStichtagCode desc) LEFT JOIN dbo.XLOVCode STI WITH (READUNCOMMITTED) on STI.LOVName = 'VmFallgewichtungStichtag' and STI.Code = VBW.VmFallgewichtungStichtagCode LEFT JOIN dbo.VmMassnahme VMN WITH (READUNCOMMITTED) on VMN.FaLeistungID = FAL.FaLeistungID and VMN.VmMassnahmeID = (select top 1 VmMassnahmeID from dbo.VmMassnahme WITH (READUNCOMMITTED) where FaLeistungID = FAL.FaLeistungID and DatumVon <= convert(datetime,STI.Text + convert(varchar,VBW.VmFallgewichtungJahr),104) order by DatumVon desc) INNER JOIN dbo.XOrgUnit_User OUU WITH (READUNCOMMITTED) on OUU.UserID = FAL.UserID and OUU.OrgUnitMemberCode = 2 INNER JOIN dbo.XOrgUnit ORG WITH (READUNCOMMITTED) on ORG.OrgUnitID = OUU.OrgUnitID AND ORG.ItemName = 'EKS Beistandschaften' INNER JOIN dbo.XUser USR WITH (READUNCOMMITTED) on USR.UserID = FAL.UserID and isMandatsTraeger = 1
where FAL.ModulID = 5 and FAL.FaProzessCode = 501 and FAL.DatumBis is null and -- aktiver Fall exists (select VmBerichtID from dbo.VmBericht WITH (READUNCOMMITTED) where FaLeistungID = FAL.FaLeistungID and Erstellung is null) -- pendente Berichterstellung --- and VBW.UserID = {edtUserID.LookupID}
-- output 1: alle aktuellen Bewertungen select SAR = USR.LastName + isNull(', ' + USR.FirstName,''), Mandant = PRS.NameVorname, ZGB = Replace(dbo.fnLOVTextListe('VmZGB',AKT.ZGBCodes),' ZGB',''), Stichdatum = AKT.Stichdatum, Falltyp = TYP.Text, bewilligt = BEW.Text, Tage = AKT.Tage, BaPersonID$ = AKT.BaPersonID, VmBewertungID$ = AKT.VmBewertungID from @Aktuell AKT inner join dbo.vwPerson PRS on PRS.BaPersonID = AKT.BaPersonID left join dbo.XUser USR WITH (READUNCOMMITTED) on USR.UserID = AKT.UserID left join dbo.XLOVCode TYP WITH (READUNCOMMITTED) on TYP.LOVName = 'vmMandatstyp' and TYP.Code = AKT.FallTypCode left join dbo.XLOVCode BEW WITH (READUNCOMMITTED) on BEW.LOVName = 'vmMandatstyp' and BEW.Code = AKT.BewilligtCode where 1 = 1 --- and AKT.BaPersonID = {edtBaPersonID.LookupID} --- and ({edtAbweichung} = 0 or isNull(AKT.FallTypCode,-1) <> isNull(AKT.BewilligtCode,-1)) order by SAR, PRS.NameVorname
