data.value:
DECLARE @VmAbkDatAuftragFldID   INT;
DECLARE @VmAbkDatAbschlussFldID INT;
DECLARE @VmAbkDurchFldID        INT;
DECLARE @VmAbkMeldeartFldID     INT;
DECLARE @VmAbkArtFldID          INT;
DECLARE @VmAbkResultatFldID     INT;

SELECT @VmAbkDatAuftragFldID    = dbo.fnDynaFieldID('VmAbkDatAuftrag');
SELECT @VmAbkDatAbschlussFldID  = dbo.fnDynaFieldID('VmAbkDatAbschluss');
SELECT @VmAbkDurchFldID         = dbo.fnDynaFieldID('VmAbkDurch');
SELECT @VmAbkMeldeartFldID      = dbo.fnDynaFieldID('VmAbkMeldeart');
SELECT @VmAbkArtFldID           = dbo.fnDynaFieldID('VmAbkArt');
SELECT @VmAbkResultatFldID      = dbo.fnDynaFieldID('VmAbkResultat');

-- Output:
SELECT VmFaLeistungID$      = FAL.FaLeistungID,
       BaPersonID$          = PRS.BaPersonID,
       [Auftrag vom]        = CONVERT(DATETIME, DAU.Value),
       [Abgeschlossen am]   = CONVERT(DATETIME, DAB.Value),
       [Abklärung durch]    = USR.NameVorname,
       [Durch]              = CONVERT(INT, CASE WHEN DUR.Value IS NULL THEN 0 ELSE 1 END),
       [Gemeinde]           = dbo.fnLOVText('GemeindeSozialdienst', FAL.GemeindeCode),
       [Gde]                = CONVERT(INT, CASE WHEN FAL.GemeindeCode IS NULL THEN 0 ELSE 1 END),
       [Meldeart]           = dbo.fnLOVText('VmAbkMeldeart', CONVERT(INT, MLD.Value)),
       [M-Art]              = CONVERT(INT, CASE WHEN MLD.Value IS NULL THEN 0 ELSE 1 END),
       [Abklärungsart]      = dbo.fnLOVText('VmAbkArt', CONVERT(INT, ABK.Value)),
       [Abkl-Art]           = CONVERT(INT, CASE WHEN ABK.Value IS NULL THEN 0 ELSE 1 END),
       [Abklärungsresultat] = dbo.fnLOVText('VmAbkResultat', CONVERT(INT, RES.Value)),
       [Resultat]           = CONVERT(INT, CASE WHEN RES.Value IS NULL THEN 0 ELSE 1 END),
       [Dossierträger]      = PRS.Name + ISNULL(', ' + PRS.Vorname, '')
FROM dbo.FaLeistung        FAL WITH (READUNCOMMITTED)
  INNER JOIN dbo.BaPerson  PRS WITH (READUNCOMMITTED) ON PRS.BaPersonID = FAL.BaPersonID
  INNER JOIN dbo.XUser     BEN WITH (READUNCOMMITTED) ON BEN.UserID = FAL.UserID
  INNER JOIN dbo.DynaValue DAU WITH (READUNCOMMITTED) ON DAU.DynaFieldID = @VmAbkDatAuftragfldID
                                                     AND DAU.FaLeistungID = FAL.FaLeistungID
  LEFT  JOIN dbo.DynaValue DAB WITH (READUNCOMMITTED) ON DAB.DynaFieldID = @VmAbkDatAbschlussFldID
                                                     AND DAB.FaLeistungID = FAL.FaLeistungID
                                                     AND DAB.GridRowID = DAU.GridRowID
  LEFT  JOIN dbo.DynaValue DUR WITH (READUNCOMMITTED) ON DUR.DynaFieldID = @VmAbkDurchFldID
                                                     AND DUR.FaLeistungID = FAL.FaLeistungID
                                                     AND DUR.GridRowID = DAU.GridRowID
  LEFT  JOIN dbo.vwUser    USR WITH (READUNCOMMITTED) ON USR.UserID = DUR.Value                                                     
  LEFT  JOIN dbo.DynaValue MLD WITH (READUNCOMMITTED) ON MLD.DynaFieldID = @VmAbkMeldeartFldID
                                                     AND MLD.FaLeistungID = FAL.FaLeistungID
                                                     AND MLD.GridRowID = DAU.GridRowID
  LEFT  JOIN dbo.DynaValue ABK WITH (READUNCOMMITTED) ON ABK.DynaFieldID = @VmAbkArtFldID
                                                     AND ABK.FaLeistungID = FAL.FaLeistungID
                                                     AND ABK.GridRowID = DAU.GridRowID
  LEFT  JOIN dbo.DynaValue RES WITH (READUNCOMMITTED) ON RES.DynaFieldID = @VmAbkResultatFldID
                                                     AND RES.FaLeistungID = FAL.FaLeistungID
                                                     AND RES.GridRowID = DAU.GridRowID
WHERE 1 = 1
---AND CONVERT(DATETIME, DAU.Value, 104) >= {edtAuftragDatumVon}
---AND CONVERT(DATETIME, DAU.Value, 104) <= {edtAuftragDatumBis}
---AND CONVERT(DATETIME, DAB.Value, 104) >= {edtAbschlussDatumVon}
---AND CONVERT(DATETIME, DAB.Value, 104) <= {edtAbschlussDatumBis}
---AND USR.UserID = {edtAbklaerungUserID.LookupID}
---AND FAL.GemeindeCode = {edtZustaendigeGemeindeCode}
---AND CONVERT(INT, MLD.Value) = {edtMeldeartCode}
---AND CONVERT(INT, ABK.Value) = {edtAbklaerungsArtCode}
---AND CONVERT(INT, RES.Value) = {edtAbklaerungsResultatCode}
  AND FAL.ModulID = 5;
