data.value:
DECLARE @LanguageCode INT; SET @LanguageCode = {0};
DECLARE @OrgGruppeID INT; --- SET @OrgGruppeID = {ctlOrgUnitTeamUser.SucheGruppe};
DECLARE @TeamID INT; --- SET @TeamID = {ctlOrgUnitTeamUser.SucheTeam};
DECLARE @UserID INT; --- SET @UserID = {ctlOrgUnitTeamUser.SucheUserID.LookupID};
DECLARE @DatumVon DATETIME SET @DatumVon = dbo.fnFirstDayOf(DATEADD(m, -5, GETDATE()))
SELECT [BaPersonID$] = PRS.BaPersonID, [BaPersonID_Leistung$] = LEI.BaPersonID, [Abfragemonat] = dbo.fnDateAsVarchar(GETDATE(), 'yyyy.mm'), [Fall-Nr.] = FAL.FaFallID, [Personen-Nr.] = PRS.BaPersonID, [Nachname] = PRS.Name, [Vorname] = PRS.Vorname, [Alter] = dbo.fnGetAgeMortal(PRS.Geburtsdatum, GETDATE(), PRS.Sterbedatum), [Anstellungsverhältnis] = dbo.fnGetLOVMLText('BaErwerbssituation', PRS.ErwerbssituationCode, @LanguageCode), [Jetzige Tätigkeit] = BER.Beruf, [Stellensuche] = dbo.fnGetLOVMLText('BaStellensuchend', PRS.StellensuchendCode, @LanguageCode), [NE-Grund] = dbo.fnGetLOVMLText('BaGrundNichtErwerbstaetig', PRS.BaGrundNichtErwerbstaetigCode, @LanguageCode), [NE-Anmeldung] = PRS.NEAnmeldung, [Pensum] = CONVERT(VARCHAR(100), NULL), [Typ] = CONVERT(VARCHAR(100), NULL), [GrundTeilzeit] = CONVERT(VARCHAR(100), NULL), [User] = USR.LogonName, [Team] = USR.OrgUnit INTO #Result FROM dbo.BgFinanzplan_BaPerson FPP WITH(READUNCOMMITTED) INNER JOIN dbo.BgFinanzplan FPL WITH(READUNCOMMITTED) ON FPL.BgFinanzplanID = FPP.BgFinanzplanID AND COALESCE(FPL.DatumBis, FPL.GeplantBis, '99991231') >= dbo.fnDateSerial(DATEPART(YEAR, GETDATE()) - 1, 1, 1) INNER JOIN dbo.FaLeistung LEI WITH(READUNCOMMITTED) ON LEI.FaLeistungID = FPL.FaLeistungID INNER JOIN dbo.FaFall FAL WITH(READUNCOMMITTED) ON FAL.FaFallID = LEI.FaFallID AND (@UserID IS NULL OR FAL.UserID = @UserID) INNER JOIN dbo.vwUser USR ON USR.UserID = FAL.UserID INNER JOIN dbo.BaPerson PRS WITH(READUNCOMMITTED) ON PRS.BaPersonID = FPP.BaPersonID LEFT JOIN dbo.BaBeruf BER WITH(READUNCOMMITTED) ON BER.BaBerufID = PRS.BerufCode WHERE FPP.IstUnterstuetzt = 1 AND ((@OrgGruppeID IS NULL AND @TeamID IS NULL) OR USR.OrgUnitID IN (SELECT OrgUnitID FROM dbo.fnOrgUnitsOfTeam(@OrgGruppeID, @TeamID))) --- AND dbo.fnGetAgeMortal(PRS.Geburtsdatum, GETDATE(), PRS.Sterbedatum) >= {edtSearchAge} GROUP BY PRS.BaPersonID, PRS.Name, PRS.Vorname, PRS.Geburtsdatum, PRS.Sterbedatum, PRS.ErwerbssituationCode, BER.Beruf, PRS.StellensuchendCode, PRS.BaGrundNichtErwerbstaetigCode, PRS.NEAnmeldung, LEI.BaPersonID, FAL.FaFallID, USR.LogonName, USR.OrgUnit
;WITH PersonenMitRelevantenBuchungen AS ( SELECT BaPersonID FROM fnGetRelevanteBuchungen(@DatumVon, GETDATE(), @UserID, NULL, NULL, @TeamID) GROUP BY BaPersonID ) DELETE RES FROM #Result RES WHERE BaPersonID$ NOT IN (SELECT BaPersonID FROM PersonenMitRelevantenBuchungen) AND BaPersonID_Leistung$ NOT IN (SELECT BaPersonID FROM PersonenMitRelevantenBuchungen)
-- ergänzen mit Arbeitssituation UPDATE RES SET [Pensum] = CASE ARB.Pensum WHEN '' THEN NULL ELSE ARB.Pensum END, [Typ] = CASE ARB.Typ WHEN '' THEN NULL ELSE ARB.Typ END, [GrundTeilzeit] = CASE ARB.GrundTeilzeit WHEN '' THEN NULL ELSE ARB.GrundTeilzeit END FROM #Result RES LEFT JOIN (SELECT BaPersonID, Typ = dbo.ConcDistinct(dbo.fnGetLOVMLText('BaArbeitTyp', TypCode, @LanguageCode)), Pensum = dbo.ConcDistinct(Pensum), GrundTeilzeit = dbo.ConcDistinct(dbo.fnGetLOVMLText('BaGrundteilzeit', BaGrundteilzeitCode, @LanguageCode)) FROM dbo.BaArbeit WITH(READUNCOMMITTED) WHERE GETDATE() BETWEEN DatumVon AND ISNULL(DatumBis,'9999-12-31') GROUP BY BaPersonID) ARB ON ARB.BaPersonID = RES.BaPersonID$
SELECT [BaPersonID$], [Abfragemonat], [Fall-Nr.] = dbo.ConcDistinct([Fall-Nr.]), [Personen-Nr.], [Nachname], [Vorname], [Alter], [Anstellungsverhältnis], [Jetzige Tätigkeit], [Stellensuche], [NE-Grund], [NE-Anmeldung], [Pensum], [Typ], [GrundTeilzeit], [User] = dbo.ConcDistinct([User]), [Team] = dbo.ConcDistinct([Team]) FROM #Result GROUP BY [BaPersonID$], [Abfragemonat], [Personen-Nr.], [Nachname], [Vorname], [Alter], [Anstellungsverhältnis], [Jetzige Tätigkeit], [Stellensuche], [NE-Grund], [NE-Anmeldung], [Pensum], [Typ], [GrundTeilzeit] ORDER BY 5,6
DROP TABLE #Result
