        #region Private Methods

        private void btnJumpTo_Click(object sender, System.EventArgs e)
        {
            if (qryQuery.Row == null) return;

            string jumpToPath = string.Format(
                "ClassName=FrmFall;" +
                "BaPersonID={0};" +
                "ModulID=3;" +
                "TreeNodeID=CtlWhFinanzplan{1}\\BBG{2};",
                qryQuery["FallBaPersonID$"],
                qryQuery["BgFinanzplanID$"],
                qryQuery["BgBudgetID$"]);

            System.Collections.Specialized.HybridDictionary dic = FormController.ConvertToDictionary(jumpToPath);
            bool result = FormController.OpenForm((string)dic["ClassName"], "Action", "JumpToPath", dic);
        }

        private void btnUndo_Click(object sender, System.EventArgs e)
        {
            if (!KissMsg.ShowQuestion("Soll die gelöschte Masterbudget-Position reaktiviert werden?")) return;

            try
            {
                DBUtil.ExecSQLThrowingException(@"
                    update BPO
                    set    DatumBis     = case when FPL.BgBewilligungStatusCode in (5,9)
                                          then FPL.DatumBis
                                          else null
                                          end,
                           BewilligtBis = case when FPL.BgBewilligungStatusCode in (5,9)
                                          then FPL.DatumBis
                                          else null
                                          end
                    from   BgPosition BPO
                           inner join BgBudget     BDG ON BDG.BgBudgetID = BPO.BgBudgetID
                           inner join BgFinanzplan FPL ON FPL.BgFinanzplanID = BDG.BgFinanzplanID
                    where BPO.BgPositionID = {0}",
                    qryQuery["BgPositionID$"]);
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo("Fehler beim Reaktivieren der Masterbudget-Position\r\n\r\n" + ex.Message);
                return;
            }

            KissMsg.ShowInfo("Die Masterbudget-Position wurde reaktiviert.");
        }

        #endregion
    }
