        private void btnDelete_Click(object sender, System.EventArgs e)
        {
            try
            {
                DBUtil.ExecSQL(@"
                    update BaZahlungsweg
                    set    AdresseName = null,
                           Adressename2 = null,
                           AdresseStrasse = null,
                           AdresseHausNr = null,
                           AdressePostfach = null,
                           AdressePLZ = null,
                           AdresseOrt = null,
                           Modifier = {1},
                           Modified = GETDATE(),
                           MutiertUserID = {2}
                    where BaZahlungswegID = {0}",
                    qryQuery["BaZahlungswegID"],
                    DBUtil.GetDBRowCreatorModifier(),
                    Session.User.UserID);

                qryQuery["EinzahlungFuer"] = null;
                qryQuery.Row.AcceptChanges();
                qryQuery.RowModified = true;
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo(ex.Message);
            }
        }

