        protected override void NewSearch()
        {
            // Default Suche setzen
            edtStatus.SelectedIndex = 0;
            // vorher verwendete Fallnummer übernehmen
            edtFallNummer.Text = Old_FallNummer;
        }

        protected override void RunSearch()
        {
            int FallNr = 0;
            FallNummer = 0;
            try
            {
                if (!DBUtil.IsEmpty(edtFallNummer.EditValue)) 
                {
                    FallNr = Convert.ToInt32(Utils.ConvertToString(edtFallNummer.EditValue));
                    FallNummer = FallNr;
                }
            }
            catch (Exception ex)
            {
                // Eintrag ins Log.
                LOG.ErrorFormat("Fehler in: [{0}]. Exception: [{1}]", GetType().FullName, ex.Message);

                // Eintrag ins Log (Ansicht unter Anwendung/Newod Schnittstelle/Protokolle)
                XLog.Create(GetType().FullName, LogLevel.ERROR, ex.Message);

                KissMsg.ShowInfo("Die Fallnummer darf nur Zahlen enthalten.");
                throw new KissCancelException();
            }
            // verwendete Fallnummer merken
            Old_FallNummer = edtFallNummer.Text;
            StatusString = Utils.ConvertToString(edtStatus.EditValue);

            object[] parameters = new object[] {FallNummer, StatusString };
            this.kissSearch.SelectParameters = parameters;
            base.RunSearch();
        }

