        private void ChangeState(int NewState)
        {
            // 09.02.2009 : sozheo : try-catch-Block korrigiert

            if (qryQuery.Count == 0) return;
            qryQuery.Post();

            int Anzahl = 0;
            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows)
            if ((bool)row["IstSelektiert"]) Anzahl += 1;

            if (Anzahl == 0)
            {
                KissMsg.ShowInfo("Sie haben keine Verfügung selektiert.");
                return;
            }

            Anzahl = 0;
            Session.BeginTransaction();
            try
            {
                foreach (System.Data.DataRow row in qryQuery.DataTable.Rows) if ((bool)row["IstSelektiert"])
                {
                    DBUtil.ExecSQL(string.Format(@"
                        Update dbo.AmVerfuegung set
                          AmVerfuegungStatusCode = {0},
                          EntscheidDatum = convert(DateTime, '{1}')
                        where AmVerfuegungID = {2}",
                        NewState,
                        DateTime.Today.ToString("yyyyMMdd"),
                        row["AmVerfuegungID"]
                    ));
                    DBUtil.ExecSQL(string.Format(@"
                        Update dbo.AmAnspruchsberechnung set
                          AmBerechnungsStatusCode = {0}
                        where AmVerfuegungID = {1}",
                        NewState,
                        row["AmVerfuegungID"]
                    ));
                    Anzahl += 1;
                }
                Session.Commit();
            }
            catch (Exception ex)
            {
                Session.Rollback();
                KissMsg.ShowInfo(ex.Message);
                return;
            }

            // Jetzt noch alle Monatszahlen nachrechnen
            MonatszahlenNeuRechnen();

            if (NewState == 3)
                KissMsg.ShowInfo(string.Format("{0} Verfügung(en) bewilligt.", Anzahl.ToString()));
            else if (NewState == 1)
                KissMsg.ShowInfo(string.Format("{0} Verfügung(en) zurückgesetzt.", Anzahl.ToString()));
            else
                KissMsg.ShowInfo(string.Format("{0} Verfügung(en) gestoppt.", Anzahl.ToString()));

            // Daten refreshen, damit soeben geänderte Daten nicht mehr angezeigt werden
            qryQuery.Refresh();
        }

        private void CtlQueryAmAnsprBerechnBewilligung_Load(object sender, System.EventArgs e)
        {
            if (tabControlSearch.SelectedTabIndex != 1) tabControlSearch.SelectedTabIndex = 1;

            grvQuery1.OptionsBehavior.Editable = true;
        }

        private void MonatszahlenNeuRechnen()
        {
            // Jetzt noch alle Monatszahle nachrechnen
            string VerfuegungIds = "";
            foreach (System.Data.DataRow row in qryQuery.DataTable.Rows) if ((bool)row["IstSelektiert"])
            {
                if (VerfuegungIds != "") VerfuegungIds = VerfuegungIds + ",";
                VerfuegungIds = VerfuegungIds + Utils.ConvertToInt(row["AmVerfuegungID"]);
            }

            if (VerfuegungIds == "") return;

            SqlQuery sqlIDs = DBUtil.OpenSQL(string.Format(@"
                Select distinct R.FaLeistungID from dbo.AmVerfuegung V WITH(READUNCOMMITTED)
                left join dbo.AmAnspruchsberechnung A WITH(READUNCOMMITTED) on A.AmVerfuegungID = V.AmVerfuegungID
                left join dbo.AmAbKind K WITH(READUNCOMMITTED) on K.AmAnspruchsberechnungID = A.AmAnspruchsberechnungID
                left join dbo.IkRechtstitel R WITH(READUNCOMMITTED) on R.IkRechtstitelID = K.IkRechtstitelID
                where V.AmVerfuegungID in ({0})",
                VerfuegungIds
            ));
            
            int FaLeistungID = 0;
            try
            {
                foreach (DataRow row in sqlIDs.DataTable.Rows)
                {
                    FaLeistungID = Utils.ConvertToInt(row["FaLeistungID"]);
                    SqlQuery sql = DBUtil.OpenSQL("EXEC dbo.spIkMonatszahlen_DetailAll {0}, 1", FaLeistungID);
                }
            }
            catch (Exception ex)
            {
                KissMsg.ShowInfo(ex.Message);
            }
        }

