        private void qryQuery_AfterFill(object sender, System.EventArgs e)
        {
            if (qryQuery.Count==0) qryQuery_PositionChanged(sender, e);
        }

        private void qryQuery_PositionChanged(object sender, System.EventArgs e)
        {
            qryAnspruch.Fill(@"
                select
                  BER.AmAnspruchsberechnungID,
                  BER.Bezeichnung,
                  BER.BerechnungDatumAb,
                  Typ = case K.BerechtigtCode
                    when 1 then 'ALBV'
                    when 2 then 'UeBH'
                    when 3 then 'KKBB'
                    else NULL
                  end,
                  Klient = PRS.NameVorname,
                  Kind = PK.NameVorname,
                  BisDatumKind = K.DatumBis,
                  Monatsanspruch = POS.Wert3,
                  MA = USR.DisplayText
                from dbo.AmAnspruchsberechnung BER WITH(READUNCOMMITTED)
                  left join dbo.FaLeistung L WITH(READUNCOMMITTED) on L.FaLeistungID = BER.FaLeistungID
                  left join dbo.AmAbKind K WITH(READUNCOMMITTED) on K.AmAnspruchsberechnungID = BER.AmAnspruchsberechnungID and (
                    (K.BerechtigtCode in (1,2) and L.FaProzessCode = 402) or -- ALBV
                    (K.BerechtigtCode = 3 and L.FaProzessCode = 404) -- KKBB
                  )
                  left join dbo.vwPerson PK on PK.BaPersonID = K.BaPersonID
                  left join dbo.AmAbPosition POS WITH(READUNCOMMITTED) on POS.AmAnspruchsberechnungID = BER.AmAnspruchsberechnungID
                    and (
                      (POS.AmAbPositionsartID = 730 and POS.AmAbKindID = K.AmAbKindID and L.FaProzessCode = 402) or  -- ALBV
                      (POS.AmAbPositionsartID = 3290 and L.FaProzessCode = 404)  -- KKBB
                    )
                  left join dbo.vwUser USR on USR.UserID = BER.UserID
                  left join dbo.vwPerson PRS on PRS.BaPersonID = L.BaPersonID
                where BER.AmVerfuegungID = {0}
                order by PRS.NameVorname, BER.BerechnungDatumAb",
                Utils.ConvertToInt(qryQuery["AmVerfuegungID"])
            );
            qryAnspruch.SelectStatement = "";

            btnJumpTo.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0);
            btnSelectAll.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0);
            btnUnSelectAll.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0);

            // Bewilligen: kann im Status angefragt und annulliert gemacht werden
            btnAccept.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0 && edtStatus.SelectedIndex != 1);

            // zurücksetzten (Status Vorbereitung): kann im Status angefragt gemacht werden
            btnZuruecksetzen.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0 && (
                edtStatus.SelectedIndex == 0 || (
                    edtStatus.SelectedIndex == 2 &&
                    DBUtil.UserHasRight("CtlQueryAmAnsprBerechnBewilligung_Zuruecksetzen")
                )
            ));

            // annulieren: kann im Status bewilligt gemacht werden
            btnReject.Enabled = (qryQuery.Count > 0 && qryAnspruch.Count > 0 && edtStatus.SelectedIndex == 1);
        }

