        #region Protected Methods

        protected override void NewSearch()
        {
            edtDocBriefDatum.DateTime = OldLetterDate;
              edtRevDatumText.Text = OldLetterText;
              edtTyp.EditValue = OldTyp;
              FilterUserID = 0;
        }

        // Letzte Bearbeitung
        // 17.06.2008 : sozheo : neu
        // 24.06.2008 : sozheo : SQL korrigiert
        // ---------------------------------------------------------
        protected override void RunSearch()
        {
            if (DBUtil.IsEmpty(edtDocBriefDatum.EditValue))
              {
            KissMsg.ShowInfo("Wählen Sie zuerst das Briefdatum.");
            throw new KissCancelException();
              }
              if (DBUtil.IsEmpty(edtRevDatumText.EditValue))
              {
            KissMsg.ShowInfo("Geben Sie zuerst den Text für den Zeitpunkt des letzten Revisionsbriefes ein.");
            throw new KissCancelException();
              }

              OldLetterDate = edtDocBriefDatum.DateTime;
              OldLetterText = edtRevDatumText.Text;
              OldTyp = Utils.ConvertToInt(edtTyp.EditValue);

              // Fixe Parameter
              object[] parameters = new object[]{
            edtDocBriefDatum.DateTime,
            edtRevDatumText.Text,
            FilterUserID,
            edtRevVon.EditValue,
            edtRevBis.EditValue,
            Utils.ConvertToInt(edtTyp.EditValue) == 1 ? 402 : 404
              };
              this.kissSearch.SelectParameters = parameters;
              // let base do stuff
              base.RunSearch();
        }

        #endregion

        #region Private Methods

        private void CtlQueryAmRevisionen_Load(object sender, System.EventArgs e)
        {
            // Letzte Bearbeitung
            // 17.06.2008 : sozheo : neu
            // -------------------------------------------------------------

            FilterUserID = Session.User.UserID;
            SqlQuery qry = DBUtil.OpenSQL("select DisplayText from vwUser where UserID = {0}", Session.User.UserID);
            edtUser.EditValue = Utils.ConvertToString(qry["DisplayText"]);
            edtDocBriefDatum.EditValue =  DateTime.Today;
            edtTyp.EditValue = 1;
            tabControlSearch.SelectedTabIndex = 1;
        }

        private void edtUser_UserModifiedFld(object sender, KiSS4.Gui.UserModifiedFldEventArgs e)
        {
            // Letzte Bearbeitung
            // 17.06.2008 : sozheo : neu
            // -------------------------------------------------------------

            string SearchString = edtUser.Text.Replace("*", "%").Replace("?", "_").Replace(" ", "%");

            if (DBUtil.IsEmpty(SearchString))
            {
                if (e.ButtonClicked)
                {
                    SearchString = "";
                }
                else
                {
                    FilterUserID = 0;
                    edtUser.EditValue = "";
                    edtUser.ToolTip = "";
                    return;
                }
            }

            KissLookupDialog dlg = new KissLookupDialog();
            e.Cancel = !dlg.SearchData(@"
              SELECT UserID$          = UserID,
                     DisplayText$     = DisplayText,
                     [Kürzel]         = LogonName,
                     [Mitarbeiter/in] = NameVorname,
                     [Org.Einheit]    = OrgUnit,
                     LogonNameVornameOrgUnit$ = LogonNameVornameOrgUnit
              FROM   vwUser
              WHERE ( DisplayText LIKE '%' + {0} + '%' or  {0} IS NULL)
                AND UserID IN (select distinct UserID from FaLeistung where ModulID in (3,4))
              ORDER BY NameVorname",
              SearchString,
              e.ButtonClicked,null,null,null);

            if (!e.Cancel)
            {
                FilterUserID = Utils.ConvertToInt(dlg[0]);
                edtUser.EditValue = Utils.ConvertToString(dlg[1]);
                edtUser.ToolTip = Utils.ConvertToString(dlg[1]);
            }
        }

