        #region Private Methods

        private void CtlQueryBaIBANVerifizieren_Search(object sender, EventArgs e)
        {
            qryQuery.DataTable.DefaultView.RowFilter = "Fehler IS NOT NULL AND Fehler <> ''";
            VerifyAllIBANs();
        }

        private void VerifyAllIBANs()
        {
            foreach (DataRow zahlweg in qryQuery.DataTable.Rows)
            {
                try
                {
                    string ibanVonSix = Belegleser.IBANConvert(zahlweg["KontoNr"] as string, zahlweg["ClearingNr"].ToString());
                    string ibanAusKiSS = zahlweg["IBANNummer"] as string;
                    if (ibanVonSix.Replace(" ", "") != ibanAusKiSS.Replace(" ", ""))
                    {
                        throw new KissInfoException(string.Format("IBAN aus KiSS {0} ist falsch, richtig wäre {1}", ibanAusKiSS, ibanVonSix));
                    }
                }
                catch (Exception ex)
                {
                    zahlweg["Fehler"] = ex.Message;
                }
                this.Refresh();
            }
        }

