        protected override void NewSearch()
        {
            edtSucheKategorie.EditValue = 1;
            edtSucheNettoBrutto.EditValue = 1;

            base.NewSearch();
        }

        protected override void RunSearch()
        {
            if (edtSucheStornoDatumVon.DateTime > edtSucheStornoDatumBis.DateTime)
            {
                KissMsg.ShowInfo("'Stornodatum von' kann nicht nach 'Stornodatum bis' sein");
                throw new Exception();
            }

            base.RunSearch();
        }

        private void qryQuery_AfterFill(object sender, EventArgs e)
        {
            if ((int)edtSucheNettoBrutto.EditValue == 1)
            {
                // Netto ist selektiert
                lblTitle.Text = "Gefundene Netto-Storno-Belege:";
            }
            else
            {
                // Brutto ist selektiert
                lblTitle.Text = "Gefundene Brutto-Storno-Belege:";
            }

            // autosize column's width
            gridView1.BestFitColumns();
        }

    }
