DECLARE @OrgGruppeID int SET @OrgGruppeID = NULL --- SET @OrgGruppeID = {ctlOrgUnitTeamUser.SucheGruppe}
DECLARE @TeamID int SET @TeamID = NULL --- SET @TeamID = {ctlOrgUnitTeamUser.SucheTeam}
-- Liste der angewählten LeistungsTypen DECLARE @ProzessCodeListe varchar(500) SET @ProzessCodeListe = N'200,300,301,302,304,210,500,201,402,404,405,406,407,408,409,410' -- Alle setzten für den Fall, dass nichts übergeben wird --- SET @ProzessCodeListe = {edtSucheFaProzessCodes} -- Wert wie er von KiSS übergeben wird SET @ProzessCodeListe = @ProzessCodeListe + ','
-- Liste der LeistungsTypen in temporäre Tabelle abfüllen DECLARE @tblFaProzessCode TABLE (FaProzessCode int) DECLARE @Pos int, @OldPos int SELECT @Pos = 1, @OldPos = 1
WHILE @Pos < len(@ProzessCodeListe) BEGIN SET @Pos = CharIndex(',', @ProzessCodeListe, @OldPos) INSERT INTO @tblFaProzessCode VALUES (SubString(@ProzessCodeListe, @OldPos, @Pos - @OldPos)) SET @OldPos = @Pos + 1 END
select Modul = case when (BUC.BgBudgetID is not null and LEI.FaProzessCode in(300)) then 'W' when (BUC.BgBudgetID is not null and LEI.FaProzessCode in(301, 302, 304, 305)) then 'WIK' when BUC.IkPositionID is not null then 'A' end, --LEI.FaProzessCode, Valuta = BUC.ValutaDatum, Fehler = BUC.PscdFehlermeldung, Klient = PRS.NameVorname, Betrag = BUC.Betrag, Auszahlungsart = AUS.ShortText, KD = case when KTS.KbKontoID is not null then 'D' else 'K' end, Kreditor = case when KTS.KbKontoID is not null then -- Debitor IsNull(DEBP.NameVorname + CHAR(13) + CHAR(10) + DEBP.WohnsitzMehrzeilig, DEBI.Name + CHAR(13) + CHAR(10) + DEBI.AdresseMehrzeilig) else -- Kreditor IsNull(BUC.BeguenstigtName + CHAR(13) + CHAR(10),'') + IsNull(BUC.BeguenstigtName2 + CHAR(13) + CHAR(10),'') + IsNull(BUC.BeguenstigtPostfach + char(13) + char(10), '') + IsNull(BUC.BeguenstigtStrasse + IsNull(' ' + BUC.BeguenstigtHausNr, '') + char(13) + char(10), '') + IsNull(IsNull(BUC.BeguenstigtPLZ + ' ', '') + IsNull(BUC.BeguenstigtOrt, '') + char(13) + char(10), '') + '** ' + EIZ.ShortText + ' **' + CHAR(13) + CHAR(10) + IsNull(IsNull(dbo.fnTnToPc(BUC.PCKontoNr),'') + ISNULL(', ' + BUC.BankName,'') + CHAR(13) + CHAR(10),'') + IsNull(BUC.BankKontoNr + CHAR(13) + CHAR(10), '') + IsNull(BUC.ReferenzNummer,'') end,
Zahlinfo = case when BUC.KbAuszahlungsArtCode = 101 then isnull(EIZ.ShortText,'') + isnull(', ' + dbo.fnTnToPc(ISNULL(BUC.PCKontoNr,BNK.PCKontoNr)),'') + isnull(', ' + BNK.Name,'') + isnull(', ' + BUC.BankKontoNr, '') + isnull(', ' + BUC.IBAN,'') else '' end + case when BUC.KbAuszahlungsArtCode = 103 then isnull(BUC.MitteilungZeile1,'') + isnull(', ' + BUC.MitteilungZeile2,'') + isnull(', ' + BUC.MitteilungZeile3,'') else '' end + case when BUC.KbAuszahlungsArtCode = 101 and ZAH.EinzahlungsscheinCode = 6 then isnull(', ' + BUC.BeguenstigtName,'') + isnull(', ' + BUC.BeguenstigtName2,'') + isnull(', ' + BUC.BeguenstigtPLZ + ' ' + BUC.BeguenstigtOrt,'') else '' end, USR.Sozialzentrum, [Team] = USR.OrgUnit, [User] = USR.NameVorname, BaPersonID$ = FAL.BaPersonID, ModulID$ = BUC.ModulID, BgFinanzplanID$ = FPL.BgFinanzplanID, BgBudgetID$ = BDG.BgBudgetID, BgPositionID$ = NULL from KbBuchung BUC left join BgBudget BDG on BDG.BgBudgetID = BUC.BgBudgetID left join BgFinanzplan FPL on FPL.BgFinanzplanID = BDG.BgFinanzplanID left join FaLeistung LEI on LEI.FaLeistungID = isnull(FPL.FaLeistungID,BUC.FaLeistungID) left join FaFall FAL on FAL.FaFallID = LEI.FaFallID left join vwUser USR on USR.UserID = LEI.UserID left join vwPerson PRS on PRS.BaPersonID = LEI.BaPersonID
left join BaBank BNK on BNK.BaBankID = BUC.BaBankID left join BaZahlungsweg ZAH on ZAH.BaZahlungswegID = BUC.BaZahlungswegID left join XLOVCode EIZ on EIZ.LOVName = 'BgEinzahlungsschein' AND EIZ.Code = ZAH.EinzahlungsscheinCode left join XLOVCode AUS on AUS.LOVName = 'KbAuszahlungsArt' AND AUS.Code = BUC.KbAuszahlungsArtCode left join KbKonto KTS on KTS.KbPeriodeID = BUC.KbPeriodeID and KTS.KontoNr = BUC.SollKtoNr and KTS.KbKontoartCodes = 20 left join KbKonto KTH on KTH.KbPeriodeID = BUC.KbPeriodeID and KTH.KontoNr = BUC.HabenKtoNr and KTH.KbKontoartCodes = 30 left join vwPerson DEBP on DEBP.BaPersonID = BUC.Schuldner_BaPersonID left join vwInstitution DEBI on DEBI.BaInstitutionID = BUC.Schuldner_BaInstitutionID where ((@OrgGruppeID IS NULL AND @TeamID IS NULL) OR USR.OrgUnitID IN (SELECT OrgUnitID FROM dbo.fnOrgUnitsOfTeam(@OrgGruppeID,@TeamID))) and KbBuchungStatusCode = 5 and BUC.ModulID in (3,4) and LEI.FaProzessCode in (SELECT FaProzessCode FROM @tblFaProzessCode) --- and USR.UserID = {ctlOrgUnitTeamUser.SucheUserID.LookupID}
