        private void CtlQueryKbZahlungseingangStatusK_Load(object sender, System.EventArgs e)
        {
            colWVCode.ColumnEdit = grdQuery1.GetLOVLookUpEdit(DBUtil.OpenSQL("SELECT Code, Text = ShortText FROM XLOVCode WHERE LOVName = 'BaWVCode'"));

            edtWVCode.Properties.DataSource = DBUtil.OpenSQL(@"
                select  Code, Text = Shorttext + ',  ' + Text
                from    XLOVCode
                where   LOVName = 'BaWVCode'
                union select null as Code, '' as Text
                order by Text");

            edtKostenart.Properties.DataSource = DBUtil.OpenSQL(@"
                SELECT Code = KontoNr, Text = KontoNr + ', ' + Name
                FROM dbo.BgKostenart
                union select null as Code, '' as Text
                ORDER BY Text"); //union select null as Text,''
        }

        private void btnAlle_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryQuery.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = true;
            }
            Cursor = Cursors.Default;
        }

        private void btnBudgetPosition_Click(object sender, System.EventArgs e)
        {
            string jumpToPath = string.Format(
                "ClassName=FrmFall;" +
                "BaPersonID={0};" +
                "ModulID=3;" +
                "TreeNodeID=CtlWhFinanzplan{1}\\BBG{2};" +
                "ActiveSQLQuery.Find=BgPositionID = {3};",
                qryQuery["FallBaPersonID"],
                qryQuery["BgFinanzplanID"],
                qryQuery["BgBudgetID"],
                qryQuery["BgPositionID"]);

            System.Collections.Specialized.HybridDictionary dic = FormController.ConvertToDictionary(jumpToPath);
            bool result = FormController.OpenForm((string)dic["ClassName"], "Action", "JumpToPath", dic);
        }

        private void btnKeine_Click(object sender, System.EventArgs e)
        {
            Cursor = Cursors.WaitCursor;

            foreach (DataRow R in qryQuery.DataTable.Rows)
            {
                if (!DBUtil.IsEmpty(R["Sel"])) R["Sel"] = false;
            }
            Cursor = Cursors.Default;
        }

