        #region Private Methods

        private void btnMonatsbudget_Click(object sender, System.EventArgs e)
        {
            if (qryQuery.Count == 0 || DBUtil.IsEmpty(qryQuery["JumpToMBPfad$"])) return;

            FormController.OpenForm("FrmFall", "Action", "JumpToNodeByFieldValue",
                        "BaPersonID", qryQuery["FallBaPersonID$"],
                        "ModulID", 5,
                        "FieldValue", qryQuery["JumpToMBPfad$"]);
        }

        private void CtlQueryKgBudgetpositionen_Load(object sender, System.EventArgs e)
        {
            //Buchungsstati laden
            repositoryItemImageComboBox1.SmallImages = KissImageList.SmallImageList;
            SqlQuery qryStati = DBUtil.OpenSQL(@"
                select Code, Text, Value1 from XLOVCode where LOVName = 'KgBuchungsStatus'
                union select -1, 'Masterbudget vorbereitet', 1381
                union select -2, 'Masterbudget abgelehnt', 1381
                union select -3, 'Masterbudget angefragt', 1382
                union select -5, 'Masterbudget bewilligt', 1383
                union select -9, 'Masterbudget gesperrt', 1384
                union select -11, 'Monatsbudget vorbereitet', 1351
                union select -15, 'Monatsbudget bewilligt', 1352
                union select -19, 'Monatsbudget gesperrt', 1357");

            foreach (DataRow R in qryStati.DataTable.Rows)
            {
                repositoryItemImageComboBox1.Items.Add(new DevExpress.XtraEditors.Controls.ImageComboBoxItem(
                    R["Text"].ToString(),
                    (int)R["Code"],
                    KissImageList.GetImageIndex(Convert.ToInt32(R["Value1"]))));
            }

            colBudgetStatus2.ColumnEdit = repositoryItemImageComboBox1;
            colPosStatus2.ColumnEdit = repositoryItemImageComboBox1;
            colPosStatusMin2.ColumnEdit = repositoryItemImageComboBox1;
        }

        #endregion
