        #region Protected Methods
        
        protected override void NewSearch()
        {
            base.NewSearch();
            ctlOrgUnitTeamUser.NewSearch();

            edtNurAktive.Checked = true;
            edtNurLaufende.Checked = true;
            edtNurAktive.Focus();
        }

		 public override void OnSearch()
		 {
			 // Workaround um F3 weiter zu reichen, da das FrameWork das OrgUnitTeamUser UserControl nicht kennt
			 try
			 {
				 if (((ContainerControl)ActiveControl).ActiveControl.Equals(ctlOrgUnitTeamUser))
					 edtNurAktive.Focus();
			 }
			 catch
			 {
			 }

			 base.OnSearch();
		 }
        #endregion

        private void qryQuery_AfterFill(object sender, EventArgs e)
        {
            try
            {
                int index = 0;
                foreach (DevExpress.XtraGrid.Columns.GridColumn col in grdQuery1.View.Columns)
                {
                    col.VisibleIndex = index++;
                }
                grdQuery1.View.Columns["SZ"].Visible = !edtNurLaufende.Checked;
                grdQuery1.View.Columns["OE"].Visible = !edtNurLaufende.Checked;
                grdQuery1.View.Columns["MA"].Visible = !edtNurLaufende.Checked;
                grdQuery1.View.Columns["BaPersonID"].Visible = !edtNurLaufende.Checked;
                grdQuery1.View.Columns["GebDat"].Visible = !edtNurLaufende.Checked;
            }
            catch {}
        }
    }
