        #region Methods

        #region Public Methods

        public override void OnSearch()
        {
            if (tabControlSearch.SelectedTab == tpgSuchen)
            {
                foreach (Control ctl in UtilsGui.AllControls(tpgSuchen))
                {
                    if (ctl is KissButtonEdit)
                    {
                        KissButtonEdit edt = (KissButtonEdit)ctl;
                        edt.CheckPendingSearchValue();
                    }
                }
            }

            base.OnSearch();
        }

        #endregion

        #region Protected Methods

        protected override void NewSearch()
        {
            base.NewSearch();

            edtKontoVon.Focus();
        }

        protected override void RunSearch()
        {
            if (DBUtil.IsEmpty(edtDatumStichtag.EditValue))
            {
                KissMsg.ShowInfo("Das Feld Stichtag muss ausgefüllt werden.");
                throw new KissCancelException("Das Feld Stichtag muss ausgefüllt werden.");
            }
            base.RunSearch();
        }

